{"output":"// LocationService.swift
// GPS and location services for Coriolis auto-fill.                              
//
// Provides:
// - Current latitude/longitude for Coriolis calculations                         
// - Altitude for density altitude corrections                                    
// - Location accuracy monitoring        
//
// SPDX-License-Identifier: BSD-2-Clause 
// Signed-off-by: Ludoplex

import Foundation
import CoreLocation
import Combine

/// Location data for ballistics calculations.                                    
public struct BallisticsLocation {       
    public let latitude: Double          
// Degrees (-90 to 90)
    public let longitude: Double         
// Degrees (-180 to 180)
    public let altitude: Double          
// Feet above sea level
    public let horizontalAccuracy: Double // Meters                               
    public let verticalAccuracy: Double   // Meters                               
    public let timestamp: Date

    public var isValid: Bool {
        return horizontalAccuracy >= 0 && horizontalAccuracy < 100                
    }

    public var isHighAccuracy: Bool {    
        return horizontalAccuracy >= 0 && horizontalAccuracy < 10                 
    }
}

/// Location service states.
public enum LocationServiceState {       
    case idle
    case requesting
    case active
    case denied
    case restricted
    case failed(Error)
}

/// Location service for GPS-based Coriolis inputs.                               
public final class LocationService: NSObject, ObservableObject {                  

    // MARK: - Published Properties      

    @Published public private(set) var currentLocation: BallisticsLocation?       
    @Published public private(set) var state: LocationServiceState = .idle        
    @Published public private(set) var authorizationStatus: CLAuthorizationStatus = .notDetermined                         

    // MARK: - Private Properties        

    private let locationManager: CLLocationManager                                
    private var updateContinuation: CheckedContinuation<BallisticsLocation, Error>?                                        

    // MARK: - Constants

    private static let metersToFeet: Double = 3.28084                             

    // MARK: - Initialization

    public override init() {
        self.locationManager = CLLocationManager()                                
        super.init()

        locationManager.delegate = self  
        locationManager.desiredAccuracy = kCLLocationAccuracyBest                 
        locationManager.distanceFilter = 10  // Update every 10 meters            

        authorizationStatus = locationManager.authorizationStatus                 
    }

    // MARK: - Public API

    /// Request location authorization.  
    public func requestAuthorization() { 
        state = .requesting
        locationManager.requestWhenInUseAuthorization()                           
    }

    /// Start continuous location updates.                                        
    public func startUpdates() {
        guard authorizationStatus == .authorizedWhenInUse ||                      
              authorizationStatus == .authorizedAlways else {                     
            state = .denied
            return
        }

        state = .active
        locationManager.startUpdatingLocation()                                   
    }

    /// Stop location updates.
    public func stopUpdates() {
        locationManager.stopUpdatingLocation()                                    
        state = .idle
    }

    /// Get a single location update (async).                                     
    public func getCurrentLocation() async throws -> BallisticsLocation {         
        guard authorizationStatus == .authorizedWhenInUse ||                      
              authorizationStatus == .authorizedAlways else {                     
            throw LocationError.notAuthorized                                     
        }

        return try await withCheckedThrowingContinuation { continuation in        
            self.updateContinuation = continuation                                
            locationManager.requestLocation()                                     
        }
    }

    /// Convert CLLocation to BallisticsLocation.                                 
    private func convert(_ location: CLLocation) -> BallisticsLocation {          
        return BallisticsLocation(       
            latitude: location.coordinate.latitude,                               
            longitude: location.coordinate.longitude,                             
            altitude: location.altitude * Self.metersToFeet,                      
            horizontalAccuracy: location.horizontalAccuracy,                      
            verticalAccuracy: location.verticalAccuracy,                          
            timestamp: location.timestamp
        )
    }
}

// MARK: - CLLocationManagerDelegate     

extension LocationService: CLLocationManagerDelegate {                            

    public func locationManager(_ manager: CLLocationManager, didUpdateLocations locations: [CLLocation]) {                
        guard let location = locations.last else { return }                       

        let ballLoc = convert(location)  
        currentLocation = ballLoc        

        // Resume async continuation if waiting                                   
        if let continuation = updateContinuation {                                
            updateContinuation = nil     
            continuation.resume(returning: ballLoc)                               
        }
    }

    public func locationManager(_ manager: CLLocationManager, didFailWithError error: Error) {                             
        state = .failed(error)

        if let continuation = updateContinuation {                                
            updateContinuation = nil     
            continuation.resume(throwing: error)                                  
        }
    }

    public func locationManagerDidChangeAuthorization(_ manager: CLLocationManager) {                                      
        authorizationStatus = manager.authorizationStatus                         

        switch authorizationStatus {     
        case .authorizedWhenInUse, .authorizedAlways:                             
            state = .idle
        case .denied:
            state = .denied
        case .restricted:
            state = .restricted
        case .notDetermined:
            state = .idle
        @unknown default:
            state = .idle
        }
    }
}

// MARK: - Errors

public enum LocationError: Error, LocalizedError {                                
    case notAuthorized
    case timeout
    case unavailable

    public var errorDescription: String? {                                        
        switch self {
        case .notAuthorized:
            return "Location access not authorized"                               
        case .timeout:
            return "Location request timed out"                                   
        case .unavailable:
            return "Location services unavailable"                                
        }
    }
