{"output":"// KestrelDevice.swift
// Bluetooth integration for Kestrel weather meters.                              
//
// Supports:
// - Kestrel 5700, 5500, 5000 series     
// - Temperature, pressure, humidity, wind speed/direction                        
// - Density altitude calculation        
//
// SPDX-License-Identifier: BSD-2-Clause 
// Signed-off-by: Ludoplex

import Foundation
import CoreBluetooth
import Combine

// MARK: - Data Structures

/// Weather data from Kestrel device.    
public struct KestrelWeatherData {       
    public let temperature: Double        // Fahrenheit                           
    public let pressure: Double          
 // inHg (station pressure)
    public let humidity: Double          
 // Percent (0-100)
    public let windSpeed: Double         
 // MPH
    public let windDirection: Double      // Degrees (0-360)                      
    public let densityAltitude: Double    // Feet                                 
    public let timestamp: Date

    public var isValid: Bool {
        return temperature > -40 && temperature < 140 &&                          
               pressure > 20 && pressure < 35 &&                                  
               humidity >= 0 && humidity <= 100                                   
    }
}

/// Kestrel device information.
public struct KestrelDeviceInfo {        
    public let identifier: UUID
    public let name: String
    public let model: KestrelModel       
    public let rssi: Int
}

/// Supported Kestrel models.
public enum KestrelModel: String, CaseIterable {                                  
    case kestrel5700 = "Kestrel 5700"    
    case kestrel5500 = "Kestrel 5500"    
    case kestrel5000 = "Kestrel 5000"    
    case unknown = "Unknown Kestrel"     

    /// Detect model from device name.   
    public static func from(name: String) -> KestrelModel {                       
        let lowered = name.lowercased()  
        if lowered.contains("5700") { return .kestrel5700 }                       
        if lowered.contains("5500") { return .kestrel5500 }                       
        if lowered.contains("5000") { return .kestrel5000 }                       
        return .unknown
    }
}

/// Kestrel connection state.
public enum KestrelConnectionState {     
    case disconnected
    case scanning
    case connecting
    case connected
    case failed(Error)
}

// MARK: - Kestrel Device Manager        

/// Manages Bluetooth connection to Kestrel weather meters.                       
public final class KestrelDevice: NSObject, ObservableObject {                    

    // MARK: - Published Properties      

    @Published public private(set) var connectionState: KestrelConnectionState = .disconnected                             
    @Published public private(set) var discoveredDevices: [KestrelDeviceInfo] = []
    @Published public private(set) var connectedDevice: KestrelDeviceInfo?        
    @Published public private(set) var latestWeatherData: KestrelWeatherData?     
    @Published public private(set) var isBluetoothAvailable: Bool = false         

    // MARK: - Private Properties        

    private var centralManager: CBCentralManager!                                 
    private var connectedPeripheral: CBPeripheral?                                
    private var weatherCharacteristic: CBCharacteristic?                          

    // MARK: - BLE UUIDs (Kestrel-specific)                                       

    // Note: These are placeholder UUIDs - real Kestrel UUIDs would need to be    
    // obtained from Kestrel's BLE documentation or reverse engineering.          
    private static let kestrelServiceUUID = CBUUID(string: "0000FFF0-0000-1000-8000-00805F9B34FB")                         
    private static let weatherDataUUID = CBUUID(string: "0000FFF1-0000-1000-8000-00805F9B34FB")                            
    private static let commandUUID = CBUUID(string: "0000FFF2-0000-1000-8000-00805F9B34FB")                                

    // MARK: - Initialization

    public override init() {
        super.init()
        centralManager = CBCentralManager(delegate: self, queue: nil)             
    }

    // MARK: - Public API

    /// Start scanning for Kestrel devices.                                       
    public func startScanning() {        
        guard isBluetoothAvailable else {
            connectionState = .failed(KestrelError.bluetoothUnavailable)          
            return
        }

        discoveredDevices.removeAll()    
        connectionState = .scanning      

        centralManager.scanForPeripherals(                                        
            withServices: [Self.kestrelServiceUUID],                              
            options: [CBCentralManagerScanOptionAllowDuplicatesKey: false]        
        )

        // Auto-stop scanning after 10 seconds                                    
        DispatchQueue.main.asyncAfter(deadline: .now() + 10) { [weak self] in     
            self?.stopScanning()
        }
    }

    /// Stop scanning for devices.       
    public func stopScanning() {
        centralManager.stopScan()        
        if case .scanning = connectionState {                                     
            connectionState = .disconnected                                       
        }
    }

    /// Connect to a discovered Kestrel device.                                   
    public func connect(to device: KestrelDeviceInfo) {                           
        guard isBluetoothAvailable else {
            connectionState = .failed(KestrelError.bluetoothUnavailable)          
            return
        }

        // Find the peripheral from discovered devices                            
        guard let peripherals = centralManager.retrievePeripherals(withIdentifiers: [device.identifier]).first else {      
            connectionState = .failed(KestrelError.deviceNotFound)                
            return
        }

        connectionState = .connecting    
        connectedPeripheral = peripherals
        centralManager.connect(peripherals, options: nil)                         
    }

    /// Disconnect from current device.  
    public func disconnect() {
        if let peripheral = connectedPeripheral {                                 
            centralManager.cancelPeripheralConnection(peripheral)                 
        }
        connectedPeripheral = nil        
        connectedDevice = nil
        weatherCharacteristic = nil      
        connectionState = .disconnected  
    }

    /// Request current weather data from connected device.                       
    public func requestWeatherData() {   
        guard let peripheral = connectedPeripheral,                               
              let characteristic = weatherCharacteristic else {                   
            return
        }

        peripheral.readValue(for: characteristic)                                 
    }

    /// Enable automatic weather data notifications.                              
    public func enableNotifications(_ enabled: Bool) {                            
        guard let peripheral = connectedPeripheral,                               
              let characteristic = weatherCharacteristic else {                   
            return
        }

        peripheral.setNotifyValue(enabled, for: characteristic)                   
    }

    // MARK: - Data Parsing

    /// Parse weather data from Kestrel BLE characteristic value.                 
    private func parseWeatherData(_ data: Data) -> KestrelWeatherData? {          
        // Note: This is a placeholder parser. Real implementation would          
        // need to match Kestrel's actual BLE data format.                        

        guard data.count >= 12 else { return nil }                                

        // Hypothetical format (would need real protocol docs):                   
        // Bytes 0-1: Temperature (0.1F units, signed int16)                      
        // Bytes 2-3: Pressure (0.01 inHg units, uint16)                          
        // Bytes 4-5: Humidity (0.1% units, uint16)                               
        // Bytes 6-7: Wind speed (0.1 MPH units, uint16)                          
        // Bytes 8-9: Wind direction (degrees, uint16)                            
        // Bytes 10-11: Density altitude (feet, int16)                            

        let bytes = [UInt8](data)        

        let tempRaw = Int16(bytes[0]) | (Int16(bytes[1]) << 8)                    
        let pressRaw = UInt16(bytes[2]) | (UInt16(bytes[3]) << 8)                 
        let humRaw = UInt16(bytes[4]) | (UInt16(bytes[5]) << 8)                   
        let windSpeedRaw = UInt16(bytes[6]) | (UInt16(bytes[7]) << 8)             
        let windDirRaw = UInt16(bytes[8]) | (UInt16(bytes[9]) << 8)               
        let daRaw = Int16(bytes[10]) | (Int16(bytes[11]) << 8)                    

        return KestrelWeatherData(       
            temperature: Double(tempRaw) / 10.0,                                  
            pressure: Double(pressRaw) / 100.0,                                   
            humidity: Double(humRaw) / 10.0,                                      
            windSpeed: Double(windSpeedRaw) / 10.0,                               
            windDirection: Double(windDirRaw),                                    
            densityAltitude: Double(daRaw),                                       
            timestamp: Date()
        )
    }
}

// MARK: - CBCentralManagerDelegate      

extension KestrelDevice: CBCentralManagerDelegate {                               

    public func centralManagerDidUpdateState(_ central: CBCentralManager) {       
        isBluetoothAvailable = (central.state == .poweredOn)                      

        if central.state != .poweredOn { 
            connectionState = .disconnected                                       
            discoveredDevices.removeAll()
        }
    }

    public func centralManager(_ central: CBCentralManager,                       
                               didDiscover peripheral: CBPeripheral,              
                               advertisementData: [String: Any],                  
                               rssi RSSI: NSNumber) {                             

        let name = peripheral.name ?? advertisementData[CBAdvertisementDataLocalNameKey] as? String ?? "Unknown"           
        let model = KestrelModel.from(name: name)                                 

        let deviceInfo = KestrelDeviceInfo(                                       
            identifier: peripheral.identifier,                                    
            name: name,
            model: model,
            rssi: RSSI.intValue
        )

        // Add if not already discovered 
        if !discoveredDevices.contains(where: { $0.identifier == peripheral.identifier }) {                                
            discoveredDevices.append(deviceInfo)                                  
        }
    }

    public func centralManager(_ central: CBCentralManager,                       
                               didConnect peripheral: CBPeripheral) {             

        let name = peripheral.name ?? "Unknown"                                   
        let model = KestrelModel.from(name: name)                                 

        connectedDevice = KestrelDeviceInfo(                                      
            identifier: peripheral.identifier,                                    
            name: name,
            model: model,
            rssi: 0
        )

        connectionState = .connected     
        peripheral.delegate = self       
        peripheral.discoverServices([Self.kestrelServiceUUID])                    
    }

    public func centralManager(_ central: CBCentralManager,                       
                               didFailToConnect peripheral: CBPeripheral,         
                               error: Error?) {                                   

        connectionState = .failed(error ?? KestrelError.connectionFailed)         
        connectedPeripheral = nil        
    }

    public func centralManager(_ central: CBCentralManager,                       
                               didDisconnectPeripheral peripheral: CBPeripheral,  
                               error: Error?) {                                   

        connectionState = .disconnected  
        connectedPeripheral = nil        
        connectedDevice = nil
        weatherCharacteristic = nil      
    }
}

// MARK: - CBPeripheralDelegate

extension KestrelDevice: CBPeripheralDelegate {                                   

    public func peripheral(_ peripheral: CBPeripheral,                            
                           didDiscoverServices error: Error?) {                   

        guard let services = peripheral.services else { return }                  

        for service in services {        
            if service.uuid == Self.kestrelServiceUUID {                          
                peripheral.discoverCharacteristics(                               
                    [Self.weatherDataUUID, Self.commandUUID],                     
                    for: service
                )
            }
        }
    }

    public func peripheral(_ peripheral: CBPeripheral,                            
                           didDiscoverCharacteristicsFor service: CBService,      
                           error: Error?) {                                       

        guard let characteristics = service.characteristics else { return }       

        for characteristic in characteristics {                                   
            if characteristic.uuid == Self.weatherDataUUID {                      
                weatherCharacteristic = characteristic                            

                // Enable notifications for live data                             
                peripheral.setNotifyValue(true, for: characteristic)              

                // Read initial value    
                peripheral.readValue(for: characteristic)                         
            }
        }
