                           error: Error?) {                                       

        guard characteristic.uuid == Self.weatherDataUUID,                        
              let data = characteristic.value else { return }                     

        if let weatherData = parseWeatherData(data) {                             
            latestWeatherData = weatherData                                       
        }
    }
}

