$clientSecret = "7d3191b7c6a5f09c092509779b11037fd9a6de613b"
$grantCode = "1000.d05c418adea9bfe0005f974574e2a9cf.3978542d5ec253960ca96270cbe71d5e"

$body = @{
    grant_type = "authorization_code"
    client_id = $clientId
    client_secret = $clientSecret
    code = $grantCode
}

Write-Host "Exchanging grant code for refresh token..."

try {
    $response = Invoke-RestMethod -Uri "https://accounts.zoho.com/oauth/v2/token" -Method Post -Body $body
    Write-Host "=== Token Exchange Successful ===" -ForegroundColor Green
    Write-Host "Access Token: $($response.access_token.Substring(0,30))..."
    Write-Host ""
    Write-Host "REFRESH TOKEN (copy this):" -ForegroundColor Yellow
    Write-Host $response.refresh_token -ForegroundColor Cyan
    Write-Host ""
    Write-Host "Expires In: $($response.expires_in) seconds"

    # Save refresh token to file
    $response.refresh_token | Out-File -FilePath "new_refresh_token.txt"
    Write-Host "Saved to new_refresh_token.txt"
} catch {
    Write-Host "Error exchanging token:" -ForegroundColor Red
    Write-Host $_.Exception.Message
    if ($_.ErrorDetails.Message) {
        Write-Host $_.ErrorDetails.Message
    }
}
"}

