#define BALLISTICS_H

#include <stdint.h>
#include <stdbool.h>
#include <stddef.h>
#include "drag_models.h"
#include "atmosphere.h"

/* Forward declaration for custom drag table */                                   
struct DragTable;
typedef struct DragTable DragTable;      

// ============================================================================   
// Constants
// ============================================================================   

#define BALLISTICS_MOA_PER_INCH_AT_100YDS  0.95493  // 1 MOA = 1.047" at 100yds   
#define BALLISTICS_MIL_PER_INCH_AT_100YDS  0.2778   // 1 MIL = 3.6" at 100yds     
#define BALLISTICS_EARTH_ANGULAR_VEL       7.2921e-5 // rad/s                     

// ============================================================================   
// Core Structures
// ============================================================================   

/**
 * Full ballistics input configuration   
 */
typedef struct {
    // Bullet properties
    double bullet_weight_grains;
    double bullet_length_inches;
    double bullet_diameter_inches;       
    double ballistic_coefficient;        
    DragModel drag_model;

    // Rifle properties
    double muzzle_velocity_fps;
    double sight_height_inches;
    double zero_range_yards;
    double twist_rate_inches;       // Positive = right twist, negative = left    
    double barrel_length_inches;

    // Environmental conditions
    AtmosphereConditions atmosphere;     

    // Target data
    double target_range_yards;
    double shot_angle_degrees;      // Positive = uphill, negative = downhill     

    // Wind data
    double wind_speed_mph;
    double wind_direction_degrees;  // 0=headwind, 90=right crosswind, 270=left   

    // Location (for Coriolis)
    double latitude_degrees;
    double azimuth_degrees;         // 0=North, 90=East, 180=South, 270=West      

    // Options
    bool enable_spin_drift;
    bool enable_coriolis;

    // Custom drag table (NULL = use standard DragModel)                          
    const DragTable* custom_drag_table;  
} BallisticsInput;

/**
 * Complete ballistics solution
 */
typedef struct {
    double range_yards;

    // Primary outputs (what shooter needs)                                       
    double drop_inches;
    double windage_inches;
    double elevation_moa;
    double windage_moa;
    double elevation_mil;
    double windage_mil;
    int elevation_clicks;           // Assuming 0.25 MOA clicks                   
    int windage_clicks;

    // Secondary outputs
    double velocity_fps;
    double energy_ftlbs;
    double time_of_flight_sec;

    // Component breakdown
    double gravity_drop_inches;
    double wind_drift_inches;
    double spin_drift_inches;
    double coriolis_vertical_inches;     
    double coriolis_horizontal_inches;   

    // Stability
    double gyroscopic_stability;    // SG factor (> 1.0 = stable)                 
} BallisticsOutput;

/**
 * Trajectory point for range card       
 */
typedef struct {
    double range_yards;
    double drop_inches;
    double drop_moa;
    double drop_mil;
    double windage_inches;
    double windage_moa;
    double windage_mil;
    double velocity_fps;
    double energy_ftlbs;
    double time_of_flight_sec;
} TrajectoryPoint;

// ============================================================================   
// Core Functions
// ============================================================================   

int ballistics_init(void);
void ballistics_cleanup(void);

/**
 * Calculate complete ballistics solution for a shot                              
 */
int ballistics_solve(const BallisticsInput* input, BallisticsOutput* output);     

/**
 * Calculate full trajectory from zero to max range                               
 * @param input Shot configuration       
 * @param max_range Maximum range in yards                                        
 * @param step Range step in yards       
 * @param points Output array (caller must allocate)                              
 * @param point_count Number of points generated                                  
 * @return 0 on success
 */
int ballistics_trajectory(const BallisticsInput* input, double max_range,         
                          double step, TrajectoryPoint* points, size_t* point_count);                                      

// ============================================================================   
// Physics Functions
// ============================================================================   

/** Calculate bullet drop at range */    
double ballistics_drop(const BallisticsInput* input, double range_yards);         

/** Calculate remaining velocity at range */                                      
double ballistics_velocity(const BallisticsInput* input, double range_yards);     

/** Calculate kinetic energy */
double ballistics_energy(double velocity_fps, double bullet_weight_grains);       

/** Calculate time of flight to range */ 
double ballistics_time_of_flight(const BallisticsInput* input, double range_yards);                                        

/** Calculate wind drift for given wind */                                        
double ballistics_wind_drift(const BallisticsInput* input, double range_yards,    
                              double wind_speed_mph, double wind_angle_degrees);  

/**
 * Calculate gyroscopic spin drift (horizontal)                                   
 * Positive = drift in direction of twist (right for right-twist barrel)          
 */
double ballistics_spin_drift(double twist_rate_inches, double bullet_length_inches,                                        
                             double bullet_diameter_inches, double muzzle_velocity_fps,                                    
                             double time_of_flight_sec);                          

/**
 * Calculate gyroscopic stability factor (SG)                                     
 * SG > 1.0 = stable, SG < 1.0 = unstable
 */
double ballistics_gyroscopic_stability(double twist_rate_inches,                  
                                        double bullet_length_inches,              
                                        double bullet_diameter_inches,            
                                        double bullet_weight_grains,              
                                        double muzzle_velocity_fps,               
                                        double air_density_lb_ft3);               

/**
 * Calculate Coriolis effect components  
 */
typedef struct {
    double vertical_inches;     // Affects elevation                              
    double horizontal_inches;   // Affects windage                                
} CoriolisEffect;

CoriolisEffect ballistics_coriolis(double range_yards, double time_of_flight_sec, 
                                   double latitude_degrees, double azimuth_degrees,                                        
                                   double muzzle_velocity_fps);                   

// ============================================================================   
// Conversion Functions
// ============================================================================   

/** Convert inches at range to MOA */    
double ballistics_inches_to_moa(double inches, double range_yards);               

/** Convert inches at range to MIL */    
double ballistics_inches_to_mil(double inches, double range_yards);               

/** Convert MOA to inches at range */    
double ballistics_moa_to_inches(double moa, double range_yards);                  

/** Convert MIL to inches at range */    
double ballistics_mil_to_inches(double mil, double range_yards);                  

/** Convert MOA to MIL */
double ballistics_moa_to_mil(double moa);

/** Convert MIL to MOA */
double ballistics_mil_to_moa(double mil);

/** Convert MOA to clicks (assumes 0.25 MOA per click) */                         
int ballistics_moa_to_clicks(double moa, double click_value_moa);                 

// ============================================================================   
// Legacy Compatibility (deprecated, use ballistics_solve instead)                
// ============================================================================   

typedef BallisticsOutput BallisticsOutputLegacy;                                  
int ballistics_calculate(const BallisticsInput* input, double range_yards,        
                         BallisticsOutput* output);                               

// ============================================================================   
// AArch64 NEON Optimized Functions (A64 assembly)                                
// ============================================================================   

#if defined(__aarch64__) || defined(_M_ARM64)                                     

/**
 * NEON-optimized Coriolis calculation   
 * Computes horizontal and vertical Coriolis deflection using SIMD                
 *
 * @param lat_rad    Latitude in radians 
 * @param az_rad     Azimuth in radians  
 * @param omega_v_t2 Precomputed: omega * velocity * t^2                          
 * @param h_out      Output: horizontal deflection (inches)                       
 * @param v_out      Output: vertical deflection (inches)                         
 */
extern void asm_coriolis_a64(double lat_rad, double az_rad, double omega_v_t2,    
                             double* h_out, double* v_out);                       

/**
 * NEON-optimized sincos
 * Computes sin and cos of an angle in a single call                              
 *
 * @param angle   Angle in radians       
 * @param sin_out Output: sin(angle)     
 * @param cos_out Output: cos(angle)     
 */
extern void asm_sincos_a64(double angle, double* sin_out, double* cos_out);       

#endif // __aarch64__ || _M_ARM64        

