/**
 * IPFS Storage Module
 * 
 * Handles decentralized storage for the Puter.js ecosystem using IPFS.
 * Works in both Node.js and browser environments.
 */

const crypto = require('crypto');

// ============================================================================
// CONFIGURATION
// ============================================================================

const CONFIG = {
    // Public IPFS gateways (fallback chain)
    gateways: [
        'https://ipfs.io/ipfs/',
        'https://gateway.pinata.cloud/ipfs/',
        'https://cloudflare-ipfs.com/ipfs/',
        'https://dweb.link/ipfs/',
        'https://gateway.ipfs.io/ipfs/'
    ],

    // IPNS names for ecosystem data
    ipnsNames: {
        registry: 'registry.puter-ecosystem.eth',
        compliance: 'compliance.puter-ecosystem.eth',
        directory: 'directory.puter-ecosystem.eth'
    },

    // Pinning services
    pinningServices: [
        { name: 'pinata', endpoint: 'https://api.pinata.cloud' },
        { name: 'web3storage', endpoint: 'https://api.web3.storage' },
        { name: 'infura', endpoint: 'https://ipfs.infura.io:5001' }
    ],

    // Timeouts
    gatewayTimeout: 10000,
    pinTimeout: 30000
};

// ============================================================================
// IPFS CONTENT ADDRESSING
// ============================================================================

/**
 * Generate a content identifier (simulates IPFS CID)
 * In production, this would use actual IPFS hashing
 */
function generateCID(content) {
    const hash = crypto.createHash('sha256').update(content).digest('hex');
    // Simulate CIDv1 format (base32)
    return 'bafybeih' + hash.substring(0, 52);
}

/**
 * Verify content matches its CID
 */
function verifyCID(content, cid) {
    const expectedCID = generateCID(content);
    return expectedCID === cid;
}

// ============================================================================
// GATEWAY ACCESS
// ============================================================================

class IPFSGateway {
    constructor(options = {}) {
        this.gateways = options.gateways || CONFIG.gateways;
        this.timeout = options.timeout || CONFIG.gatewayTimeout;
        this.currentGateway = 0;
    }

    /**
     * Fetch content from IPFS by CID
     * Tries multiple gateways with fallback
     */
    async fetch(cid, options = {}) {
        const errors = [];

        for (let i = 0; i < this.gateways.length; i++) {
            const gatewayIndex = (this.currentGateway + i) % this.gateways.length;
            const gateway = this.gateways[gatewayIndex];

            try {
                const response = await this._fetchWithTimeout(
                    gateway + cid,
                    this.timeout
                );

                if (response.ok) {
                    const content = await response.text();

                    // Verify content integrity
                    if (options.verify !== false && !verifyCID(content, cid)) {
                        throw new Error('Content verification failed');
                    }

                    // Remember successful gateway
                    this.currentGateway = gatewayIndex;

                    return {
                        content,
                        gateway,
                        cid,
                        verified: true
                    };
                }

                errors.push({ gateway, error: `HTTP ${response.status}` });
            } catch (error) {
                errors.push({ gateway, error: error.message });
            }
        }

        throw new Error(`All gateways failed: ${JSON.stringify(errors)}`);
    }

    /**
     * Fetch with timeout
     */
    async _fetchWithTimeout(url, timeout) {
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), timeout);

        try {
            const response = await fetch(url, { signal: controller.signal });
            clearTimeout(timeoutId);
            return response;
        } catch (error) {
            clearTimeout(timeoutId);
            throw error;
        }
    }

    /**
     * Resolve IPNS name to CID
     */
    async resolveIPNS(name) {
        // In production, this would resolve via DNSLink or IPNS
        // For now, we simulate with a gateway endpoint
        const url = `https://ipfs.io/ipns/${name}`;

        try {
            const response = await this._fetchWithTimeout(url, this.timeout);
            if (response.ok) {
                return response.headers.get('X-Ipfs-Path') || null;
            }
        } catch (error) {
            console.warn(`IPNS resolution failed: ${error.message}`);
        }

        return null;
    }
}

// ============================================================================
// CONTENT PINNING
// ============================================================================

class IPFSPinner {
    constructor(options = {}) {
        this.services = options.services || CONFIG.pinningServices;
        this.credentials = options.credentials || {};
    }

    /**
     * Pin content to IPFS via pinning service
     */
    async pin(content, options = {}) {
        const cid = generateCID(content);
        const results = [];

        for (const service of this.services) {
            if (!this.credentials[service.name]) continue;

            try {
                const result = await this._pinToService(service, content, options);
                results.push({
                    service: service.name,
                    success: true,
                    cid: result.cid || cid,
                    url: result.url
                });
            } catch (error) {
                results.push({
                    service: service.name,
                    success: false,
                    error: error.message
                });
            }
        }

        return {
            cid,
            pinned: results.filter(r => r.success).length > 0,
            results
        };
    }

    /**
     * Pin to specific service
     */
    async _pinToService(service, content, options) {
        const credentials = this.credentials[service.name];

        switch (service.name) {
            case 'pinata':
                return this._pinToPinata(content, credentials, options);
            case 'web3storage':
                return this._pinToWeb3Storage(content, credentials, options);
            case 'infura':
                return this._pinToInfura(content, credentials, options);
            default:
                throw new Error(`Unknown pinning service: ${service.name}`);
        }
    }

    async _pinToPinata(content, credentials, options) {
        const response = await fetch('https://api.pinata.cloud/pinning/pinJSONToIPFS', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${credentials.jwt}`
            },
            body: JSON.stringify({
                pinataContent: JSON.parse(content),
                pinataMetadata: {
                    name: options.name || 'puter-ecosystem-data'
                }
            })
        });

        if (!response.ok) {
            throw new Error(`Pinata error: ${response.status}`);
        }

        const result = await response.json();
        return {
            cid: result.IpfsHash,
            url: `https://gateway.pinata.cloud/ipfs/${result.IpfsHash}`
        };
    }

    async _pinToWeb3Storage(content, credentials, options) {
        const response = await fetch('https://api.web3.storage/upload', {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${credentials.token}`,
                'Content-Type': 'application/json'
            },
            body: content
        });

        if (!response.ok) {
            throw new Error(`Web3.Storage error: ${response.status}`);
        }

        const result = await response.json();
        return {
            cid: result.cid,
            url: `https://${result.cid}.ipfs.dweb.link/`
        };
    }

    async _pinToInfura(content, credentials, options) {
        const auth = Buffer.from(`${credentials.projectId}:${credentials.projectSecret}`).toString('base64');

        const formData = new FormData();
        formData.append('file', new Blob([content], { type: 'application/json' }));

        const response = await fetch('https://ipfs.infura.io:5001/api/v0/add', {
            method: 'POST',
            headers: {
                'Authorization': `Basic ${auth}`
            },
            body: formData
        });

        if (!response.ok) {
            throw new Error(`Infura error: ${response.status}`);
        }

        const result = await response.json();
        return {
            cid: result.Hash,
            url: `https://ipfs.infura.io/ipfs/${result.Hash}`
        };
    }
}

// ============================================================================
// ECOSYSTEM DATA MANAGER
// ============================================================================

class EcosystemIPFS {
    constructor(options = {}) {
        this.gateway = new IPFSGateway(options);
        this.pinner = new IPFSPinner(options);
        this.cache = new Map();
        this.cacheTimeout = options.cacheTimeout || 300000; // 5 minutes
    }

    /**
     * Get current registry from IPFS
     */
    async getRegistry() {
        return this._fetchCached('registry', async () => {
            const cid = await this.gateway.resolveIPNS(CONFIG.ipnsNames.registry);
            if (cid) {
                const { content } = await this.gateway.fetch(cid);
                return JSON.parse(content);
            }
            return null;
        });
    }

    /**
     * Get compliance data from IPFS
     */
    async getCompliance() {
        return this._fetchCached('compliance', async () => {
            const cid = await this.gateway.resolveIPNS(CONFIG.ipnsNames.compliance);
            if (cid) {
                const { content } = await this.gateway.fetch(cid);
                return JSON.parse(content);
            }
            return null;
        });
    }

    /**
     * Get app directory from IPFS
     */
    async getDirectory() {
        return this._fetchCached('directory', async () => {
            const cid = await this.gateway.resolveIPNS(CONFIG.ipnsNames.directory);
            if (cid) {
                const { content } = await this.gateway.fetch(cid);
                return JSON.parse(content);
            }
            return null;
        });
    }

    /**
     * Publish new registry to IPFS
     */
    async publishRegistry(registry) {
        const content = JSON.stringify(registry, null, 2);
        return this.pinner.pin(content, { name: 'puter-ecosystem-registry' });
    }

    /**
     * Publish compliance attestation to IPFS
     */
    async publishAttestation(attestation) {
        const content = JSON.stringify(attestation, null, 2);
        return this.pinner.pin(content, { name: 'puter-ecosystem-attestation' });
    }

    /**
     * Fetch with caching
     */
    async _fetchCached(key, fetcher) {
        const cached = this.cache.get(key);
        if (cached && Date.now() - cached.timestamp < this.cacheTimeout) {
            return cached.data;
        }

        const data = await fetcher();
        this.cache.set(key, { data, timestamp: Date.now() });
        return data;
    }

    /**
     * Clear cache
     */
    clearCache() {
        this.cache.clear();
    }
}

// ============================================================================
// EXPORTS
// ============================================================================

module.exports = {
    CONFIG,
    generateCID,
    verifyCID,
    IPFSGateway,
    IPFSPinner,
    EcosystemIPFS
};

