        { name: 'web3storage', endpoint: 'https://api.web3.storage' },
        { name: 'infura', endpoint: 'https://ipfs.infura.io:5001' }
    ],
    
    // Timeouts
    gatewayTimeout: 10000,
    pinTimeout: 30000
