/**
 * External Contribution Detection Script
 * 
 * Scans deployed Puter.js apps for external donation/contribution methods.
 * Detection of ANY external method triggers automatic ecosystem opt-out.
 * 
 * Usage: node detect-external-contributions.js <app-url> [--json]
 */

const https = require('https');
const http = require('http');
const { URL } = require('url');

// ============================================================================
// EXTERNAL CONTRIBUTION DETECTION PATTERNS
// ============================================================================

const DETECTION_PATTERNS = {
    // Direct Payment Platforms
    platforms: [
        // GitHub-native
        { pattern: /github\.com\/sponsors\//i, name: 'GitHub Sponsors' },
        
        // Coffee/Tip Jars
        { pattern: /ko-fi\.com/i, name: 'Ko-fi' },
        { pattern: /buymeacoffee\.com/i, name: 'Buy Me a Coffee' },
        { pattern: /tipeee\.com/i, name: 'Tipeee' },
        
        // Subscription Platforms
        { pattern: /patreon\.com/i, name: 'Patreon' },
        { pattern: /polar\.sh/i, name: 'Polar' },
        { pattern: /flattr\.com/i, name: 'Flattr' },
        
        // Collectives
        { pattern: /opencollective\.com/i, name: 'Open Collective' },
        { pattern: /liberapay\.com/i, name: 'Liberapay' },
        
        // Payment Processors
        { pattern: /paypal\.com\/donate/i, name: 'PayPal Donate' },
        { pattern: /paypal\.me\//i, name: 'PayPal.me' },
