            path: parsedUrl.pathname + parsedUrl.search,
            method: 'GET',
            headers: {
                'User-Agent': 'Puter-Ecosystem-Compliance-Scanner/1.0'
            }
        };
        
        const req = protocol.request(options, (res) => {
            let data = '';
            res.on('data', chunk => data += chunk);
            res.on('end', () => resolve({ statusCode: res.statusCode, body: data }));
        });
        
        req.on('error', reject);
        req.setTimeout(10000, () => {
            req.destroy();
            reject(new Error('Request timeout'));
        });
        req.end();
    });
}

// ============================================================================
// CLI INTERFACE
// ============================================================================

async function main() {
    const args = process.argv.slice(2);
    
    if (args.length === 0) {
        console.log('Puter.js Ecosystem - External Contribution Detector');
        console.log('');
        console.log('Usage:');
        console.log('  node detect-external-contributions.js <url>');
        console.log('  node detect-external-contributions.js <url> --json');
        console.log('  node detect-external-contributions.js --scan-file <path>');
        console.log('');
        console.log('Examples:');
        console.log('  node detect-external-contributions.js https://user.github.io/app');
        console.log('  node detect-external-contributions.js ./index.html --scan-file');
        process.exit(0);
    }
    
    const jsonOutput = args.includes('--json');
    const scanFile = args.includes('--scan-file');
    const target = args.find(a => !a.startsWith('--'));
    
    if (!target) {
        console.error('Error: No target URL or file specified');
        process.exit(1);
    }
    
    const detector = new ExternalContributionDetector({ verbose: !jsonOutput });
    
    try {
        let content;
        
        if (scanFile) {
            const fs = require('fs');
            content = fs.readFileSync(target, 'utf-8');
        } else {
            if (!jsonOutput) console.log(`Scanning: ${target}`);
            const response = await fetchUrl(target);
            
            if (response.statusCode !== 200) {
                throw new Error(`HTTP ${response.statusCode}`);
            }
            
            content = response.body;
        }
        
        const results = detector.scan(content);
        
        if (jsonOutput) {
            console.log(JSON.stringify(results, null, 2));
        } else {
            console.log('');
            console.log('='.repeat(60));
            console.log('COMPLIANCE REPORT');
            console.log('='.repeat(60));
            console.log(`Ecosystem Footer: ${results.hasEcosystemFooter ? 'PRESENT' : 'MISSING'}`);
            console.log(`External Methods: ${results.externalMethodsDetected ? 'DETECTED' : 'NONE'}`);
            console.log(`Compliance Status: ${results.compliant ? 'COMPLIANT' : 'NON-COMPLIANT'}`);
            console.log('');
            console.log(`Summary: ${results.summary}`);
            
            if (results.findings.length > 0) {
                console.log('');
                console.log('Findings:');
                for (const finding of results.findings) {
                    console.log(`  - [${finding.category}] ${finding.method}`);
                }
            }
            console.log('='.repeat(60));
        }
        
        // Exit with appropriate code
        process.exit(results.compliant ? 0 : 1);
        
    } catch (error) {
        if (jsonOutput) {
            console.log(JSON.stringify({ error: error.message }));
        } else {
            console.error(`Error: ${error.message}`);
        }
        process.exit(2);
    }
}

// Export for use as module
module.exports = {
    ExternalContributionDetector,
    DETECTION_PATTERNS,
    ALLOWED_PATTERNS
};

// Run CLI if called directly
if (require.main === module) {
    main();
}

