const crypto = require('crypto');
const https = require('https');
const http = require('http');

// ============================================================================
// CONFIGURATION
// ============================================================================

const PRIVACY_CONFIG = {
    // I2P Configuration
    i2p: {
        samBridge: '127.0.0.1:7656',
        httpProxy: '127.0.0.1:4444',
