    lokinet: {
        socksProxy: '127.0.0.1:9050',
        snodeAddress: null
    },
    
    // Timeouts
    connectionTimeout: 30000,
    requestTimeout: 60000
};

// ============================================================================
// I2P INTEGRATION
// ============================================================================

class I2PClient {
    constructor(options = {}) {
        this.samBridge = options.samBridge || PRIVACY_CONFIG.i2p.samBridge;
        this.httpProxy = options.httpProxy || PRIVACY_CONFIG.i2p.httpProxy;
        this.destination = null;
        this.connected = false;
    }
    
    /**
     * Connect to I2P SAM bridge
     */
    async connect() {
        // In production, this would establish a SAM session
        // SAM (Simple Anonymous Messaging) is I2P's API
        
        this.connected = true;
        this.destination = this.generateDestination();
        
        return {
            connected: true,
            destination: this.destination,
            b32Address: this.destinationToB32(this.destination)
        };
    }
    
    /**
     * Generate I2P destination (simulated)
     */
    generateDestination() {
        // Real I2P destinations are much longer (516 bytes base64)
        return crypto.randomBytes(256).toString('base64');
    }
    
    /**
     * Convert destination to b32 address
     */
    destinationToB32(destination) {
        const hash = crypto.createHash('sha256').update(destination).digest();
        return hash.toString('base32').toLowerCase().substring(0, 52) + '.b32.i2p';
    }
    
    /**
     * Fetch URL via I2P proxy
     */
    async fetch(url, options = {}) {
        return new Promise((resolve, reject) => {
            const [proxyHost, proxyPort] = this.httpProxy.split(':');
            
            const requestOptions = {
                hostname: proxyHost,
                port: parseInt(proxyPort),
                path: url,
                method: options.method || 'GET',
                headers: {
                    ...options.headers,
                    'User-Agent': 'Puter-Ecosystem-I2P/1.0'
                }
            };
            
