                let data = '';
                res.on('data', chunk => data += chunk);
                res.on('end', () => resolve({
                    status: res.statusCode,
                    headers: res.headers,
                    body: data
                }));
            });
            
            req.on('error', reject);
            req.setTimeout(PRIVACY_CONFIG.requestTimeout, () => {
                req.destroy();
                reject(new Error('I2P request timeout'));
            });
            
            if (options.body) {
                req.write(options.body);
            }
            
            req.end();
        });
    }
    
