codeBlocks.content:
/**
 * Puter.js Ecosystem Footer Script
 * 
 * Provides the exclusive contribution footer functionality.
 * This is the ONLY permitted contribution method for ecosystem-compliant apps.
 * 
 * Using ANY other donation/contribution method will automatically opt the app
 * out of all ecosystem benefits.
 */

(function() {
    'use strict';

    // ========================================================================
    // CONFIGURATION
    // ========================================================================

    const ECOSYSTEM_CONFIG = {
        version: '2.0.0',
        templateRepo: 'https://github.com/ludoplex/puter-app-template',
        contributionUrl: 'https://github.com/sponsors/ludoplex',
        directoryUrl: 'https://ludoplex.github.io/puter-app-template/DIRECTORY.html',
        apiEndpoint: 'https://ludoplex.github.io/puter-app-template/ecosystem/directory.json',
        storageKey: 'puter-ecosystem-state'
    };

    // ========================================================================
    // UTILITY FUNCTIONS
    // ========================================================================

    function generateAppId() {
        // Generate a unique app ID based on current URL
        const url = window.location.hostname + window.location.pathname;
        let hash = 0;
        for (let i = 0; i < url.length; i++) {
            const char = url.charCodeAt(i);
            hash = ((hash << 5) - hash) + char;
            hash = hash & hash;
        }
        return 'pjs_' + Math.abs(hash).toString(36);
    }

    function getState() {
        try {
            const saved = localStorage.getItem(ECOSYSTEM_CONFIG.storageKey);
            return saved ? JSON.parse(saved) : { view: 'full' };
        } catch {
            return { view: 'full' };
        }
    }

    function setState(state) {
        try {
            localStorage.setItem(ECOSYSTEM_CONFIG.storageKey, JSON.stringify(state));
        } catch {
            // Storage not available
        }
    }

    // ========================================================================
    // FOOTER CREATION
    // ========================================================================

    function createEcosystemFooter(options = {}) {
        const appId = options.appId || generateAppId();
        const state = getState();

        // Don't render if hidden
        if (state.view === 'hidden') {
            logEcosystemInfo(appId);
            return null;
        }

        const footer = document.createElement('footer');
        footer.className = 'puter-ecosystem-footer' + (state.view === 'mini' ? ' eco-minimized' : '');
        footer.setAttribute('data-ecosystem-version', ECOSYSTEM_CONFIG.version);
        footer.setAttribute('data-app-id', appId);

        // Build contribution URL with tracking
        const contributionUrl = ECOSYSTEM_CONFIG.contributionUrl + '?ref=' + appId;

        footer.innerHTML = `
            <!-- Full View -->
            <div class="eco-full-view" style="display: flex; align-items: center; gap: 0.75rem; flex-wrap: wrap;">
                <span class="eco-built-with">
                    Built with <a href="https://puter.com" target="_blank" rel="noopener">Puter.js</a>
                </span>

                <span class="eco-sep">|</span>

                <a href="${contributionUrl}" 
                   target="_blank" 
                   rel="noopener"
                   class="ecosystem-contribution-link"
                   data-contribution-tracked="true">
                    <span class="eco-heart">&#9829;</span>
                    <span>Support Ecosystem</span>
                </a>

                <span class="eco-sep">|</span>

                <a href="${ECOSYSTEM_CONFIG.directoryUrl}" 
                   target="_blank" 
                   rel="noopener"
                   class="eco-directory-link">
                    Explore Apps
                </a>

                <div class="eco-controls">
                    <button onclick="window.__puterEcosystem.toggle()" title="Minimize" aria-label="Minimize footer">
                        <svg width="10" height="10" viewBox="0 0 10 10" fill="currentColor">
                            <rect y="4" width="10" height="2"/>
                        </svg>
                    </button>
                    <button onclick="window.__puterEcosystem.hide()" title="Hide" aria-label="Hide footer">
                        <svg width="10" height="10" viewBox="0 0 10 10" fill="currentColor">
                            <path d="M1 1L9 9M9 1L1 9" stroke="currentColor" stroke-width="1.5" fill="none"/>
                        </svg>
                    </button>
                </div>
            </div>

            <!-- Minimized View -->
            <div class="eco-mini-view">
                <a href="${ECOSYSTEM_CONFIG.templateRepo}" target="_blank" rel="noopener">
                    Puter.js Ecosystem
                </a>
                <span class="eco-sep">|</span>
                <a href="${contributionUrl}" target="_blank" rel="noopener" class="ecosystem-contribution-link" style="padding: 0.125rem 0.5rem;">
                    <span class="eco-heart">&#9829;</span>
                </a>
                <div class="eco-controls">
                    <button onclick="window.__puterEcosystem.toggle()" title="Expand" aria-label="Expand footer">
                        <svg width="10" height="10" viewBox="0 0 10 10" fill="currentColor">
                            <rect y="4" width="10" height="2"/>
                            <rect x="4" width="2" height="10"/>
                        </svg>
                    </button>
                    <button onclick="window.__puterEcosystem.hide()" title="Hide" aria-label="Hide footer">
                        <svg width="10" height="10" viewBox="0 0 10 10" fill="currentColor">
                            <path d="M1 1L9 9M9 1L1 9" stroke="currentColor" stroke-width="1.5" fill="none"/>
                        </svg>
                    </button>
                </div>
            </div>
        `;

        return footer;
    }

    // ========================================================================
    // ECOSYSTEM API
    // ========================================================================

    function logEcosystemInfo(appId) {
        console.info(
            '%c Puter.js Ecosystem ',
            'background: #6366f1; color: white; font-weight: bold; padding: 2px 6px; border-radius: 3px;',
            '\n\nApp ID:', appId,
            '\nTemplate:', ECOSYSTEM_CONFIG.templateRepo,
            '\nDirectory:', ECOSYSTEM_CONFIG.directoryUrl
        );
    }

    // Public API
    window.__puterEcosystem = {
        version: ECOSYSTEM_CONFIG.version,
        config: ECOSYSTEM_CONFIG,

        toggle: function() {
            const footer = document.querySelector('.puter-ecosystem-footer');
            if (!footer) return;

            footer.classList.toggle('eco-minimized');
            const isMinimized = footer.classList.contains('eco-minimized');
            setState({ view: isMinimized ? 'mini' : 'full' });
        },

        hide: function() {
            const footer = document.querySelector('.puter-ecosystem-footer');
            if (footer) footer.style.display = 'none';
            setState({ view: 'hidden' });
        },

        show: function() {
            const footer = document.querySelector('.puter-ecosystem-footer');
            if (footer) footer.style.display = '';
            setState({ view: 'full' });
        },

        getAppId: function() {
            const footer = document.querySelector('.puter-ecosystem-footer');
            return footer ? footer.getAttribute('data-app-id') : generateAppId();
        },

        // Fetch ecosystem directory
        getDirectory: async function() {
            const response = await fetch(ECOSYSTEM_CONFIG.apiEndpoint);
            return response.json();
        },

        // Get apps by category
        getAppsByCategory: async function(category) {
            const directory = await this.getDirectory();
            return directory.apps.filter(app => app.category === category);
        },

        // Open another ecosystem app with referral
        openApp: function(appUrl, params = {}) {
            const url = new URL(appUrl);
            url.searchParams.set('ref', this.getAppId());
            Object.entries(params).forEach(([k, v]) => url.searchParams.set(k, v));
            window.open(url.toString(), '_blank');
        }
    };

    // ========================================================================
    // AUTO-INITIALIZATION
    // ========================================================================

    function init() {
        // Check if footer already exists
        if (document.querySelector('.puter-ecosystem-footer')) {
            return;
        }

        // Load styles if not already loaded
        if (!document.querySelector('link[href*="ecosystem/footer.css"]')) {
            const styleContent = `
                .puter-ecosystem-footer {
                    --eco-bg: #1a1a2e;
                    --eco-text: rgba(255, 255, 255, 0.7);
                    --eco-text-hover: rgba(255, 255, 255, 0.95);
                    --eco-accent: #6366f1;
                    --eco-contribution: #f43f5e;
                    --eco-contribution-hover: #fb7185;
                    --eco-border: rgba(255, 255, 255, 0.08);
                    --eco-bg-alt: #16213e;
                    position: relative;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    flex-wrap: wrap;
                    gap: 0.75rem;
                    padding: 0.625rem 1rem;
                    background: var(--eco-bg);
                    border-top: 1px solid var(--eco-border);
                    font-family: system-ui, -apple-system, sans-serif;
                    font-size: 0.75rem;
                    color: var(--eco-text);
                }
                @media (prefers-color-scheme: light) {
                    .puter-ecosystem-footer:not([data-theme="dark"]) {
                        --eco-bg: #f8fafc;
                        --eco-text: rgba(0, 0, 0, 0.6);
                        --eco-text-hover: rgba(0, 0, 0, 0.9);
                        --eco-border: rgba(0, 0, 0, 0.08);
                        --eco-bg-alt: #f1f5f9;
                    }
                }
                .puter-ecosystem-footer a { color: inherit; text-decoration: none; transition: color 0.15s; }
                .puter-ecosystem-footer a:hover { color: var(--eco-text-hover); }
                .puter-ecosystem-footer .eco-sep { opacity: 0.3; }
                .puter-ecosystem-footer .eco-built-with a { color: var(--eco-accent); }
                .puter-ecosystem-footer .ecosystem-contribution-link {
                    display: inline-flex; align-items: center; gap: 0.375rem;
                    padding: 0.25rem 0.625rem; background: var(--eco-contribution);
                    color: white !important; border-radius: 9999px; font-weight: 500;
                    transition: background 0.15s, transform 0.15s;
                }
                .puter-ecosystem-footer .ecosystem-contribution-link:hover {
                    background: var(--eco-contribution-hover); transform: translateY(-1px);
                }
                .puter-ecosystem-footer .eco-heart { animation: eco-pulse 1.5s ease-in-out infinite; }
                @keyframes eco-pulse { 0%, 100% { transform: scale(1); } 50% { transform: scale(1.15); } }
                .puter-ecosystem-footer .eco-directory-link {
                    padding: 0.125rem 0.5rem; background: var(--eco-bg-alt); border-radius: 0.25rem;
                }
                .puter-ecosystem-footer .eco-controls { display: flex; gap: 0.125rem; margin-left: 0.5rem; }
                .puter-ecosystem-footer .eco-controls button {
                    width: 1.25rem; height: 1.25rem; padding: 0; background: transparent;
                    border: none; border-radius: 0.25rem; color: var(--eco-text);
                    cursor: pointer; opacity: 0.5; display: flex; align-items: center; justify-content: center;
                }
                .puter-ecosystem-footer .eco-controls button:hover { opacity: 1; background: var(--eco-border); }
                .puter-ecosystem-footer.eco-minimized .eco-full-view { display: none !important; }
                .puter-ecosystem-footer.eco-minimized .eco-mini-view { display: flex !important; }
                .puter-ecosystem-footer .eco-mini-view { display: none; align-items: center; gap: 0.5rem; }
                @media (max-height: 300px) { .puter-ecosystem-footer { display: none; } }
                @media (max-width: 480px) { .puter-ecosystem-footer .eco-controls { display: none; } }
            `;
            const style = document.createElement('style');
            style.textContent = styleContent;
            document.head.appendChild(style);
        }

        // Create and append footer
        const footer = createEcosystemFooter();
        if (footer) {
            document.body.appendChild(footer);
            logEcosystemInfo(footer.getAttribute('data-app-id'));
        }
    }

    // Initialize when DOM is ready
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', init);
    } else {
        init();
    }

})();

