    #!/usr/bin/env node

    const fs = require('fs');
    const path = require('path');

    // Regex patterns to detect common external donation/contribution links
    const EXTERNAL_DONATION_PATTERNS = [
        /ko-fi\.com\/\w+/i,
        /patreon\.com\/\w+/i,
        /paypal\.me\/\w+/i,
        /buymeacoffee\.com\/\w+/i,
        /github\.com\/sponsors\/\w+/i,
        // Add more patterns as needed for other platforms
    ];

    function detectExternalContributions(htmlContent) {
        const detectedLinks = [];
        for (const pattern of EXTERNAL_DONATION_PATTERNS) {
            let match;
            while ((match = pattern.exec(htmlContent)) !== null) {
                detectedLinks.push(match[0]);
            }
        }
        return detectedLinks;
    }

    // Example usage (for local testing or integration into a larger script)
    if (require.main === module) {
        const filePath = process.argv[2];
        if (!filePath) {
            console.error('Usage: node detect-external-contributions.js <path_to_index.html>');
            process.exit(1);
        }

        try {
            const htmlContent = fs.readFileSync(filePath, 'utf-8');
            const externalLinks = detectExternalContributions(htmlContent);

            if (externalLinks.length > 0) {
                console.log('External contribution links detected:');
                externalLinks.forEach(link => console.log(`- ${link}`));
                process.exit(1); // Indicate non-compliance
            } else {
                console.log('No external contribution links detected.');
                process.exit(0); // Indicate compliance
            }
        } catch (error) {
            console.error(`Error reading file ${filePath}: ${error.message}`);
            process.exit(1);
        }
    }
