 * External Contribution Detection Script
 * 
 * Scans deployed Puter.js apps for external donation/contribution methods.
 * Detection of ANY external method triggers automatic ecosystem opt-out.
 * 
 * Usage: node detect-external-contributions.js <app-url> [--json]
 */

const https = require('https');
const http = require('http');
const { URL } = require('url');

// ============================================================================
// EXTERNAL CONTRIBUTION DETECTION PATTERNS
// ============================================================================

const DETECTION_PATTERNS = {
    // Direct Payment Platforms
    platforms: [
        // GitHub-native
        { pattern: /github\.com\/sponsors\//i, name: 'GitHub Sponsors' },
        
        // Coffee/Tip Jars
        { pattern: /ko-fi\.com/i, name: 'Ko-fi' },
        { pattern: /buymeacoffee\.com/i, name: 'Buy Me a Coffee' },
        { pattern: /tipeee\.com/i, name: 'Tipeee' },
        
        // Subscription Platforms
        { pattern: /patreon\.com/i, name: 'Patreon' },
        { pattern: /polar\.sh/i, name: 'Polar' },
        { pattern: /flattr\.com/i, name: 'Flattr' },
        
        // Collectives
        { pattern: /opencollective\.com/i, name: 'Open Collective' },
        { pattern: /liberapay\.com/i, name: 'Liberapay' },
        
        // Payment Processors
        { pattern: /paypal\.com\/donate/i, name: 'PayPal Donate' },
        { pattern: /paypal\.me\//i, name: 'PayPal.me' },
        { pattern: /stripe\.com\/checkout/i, name: 'Stripe Checkout' },
        { pattern: /buy\.stripe\.com(?!.*\?ref=puter-ecosystem)/i, name: 'Stripe Payment Link (non-ecosystem)' },
        { pattern: /gumroad\.com/i, name: 'Gumroad' },
        { pattern: /lemonsqueezy\.com/i, name: 'LemonSqueezy' },
        
        // Bank/Wire
        { pattern: /donorbox\.org/i, name: 'Donorbox' },
        { pattern: /wise\.com\/pay/i, name: 'Wise Payment' },
        { pattern: /payoneer\.com/i, name: 'Payoneer' },
        
        // P2P Apps
        { pattern: /venmo\.com/i, name: 'Venmo' },
        { pattern: /cash\.app\//i, name: 'Cash App' },
        { pattern: /zelle/i, name: 'Zelle' },
        
        // Crowdfunding
        { pattern: /gofundme\.com/i, name: 'GoFundMe' },
        { pattern: /justgiving\.com/i, name: 'JustGiving' },
        { pattern: /kickstarter\.com/i, name: 'Kickstarter' },
        { pattern: /indiegogo\.com/i, name: 'Indiegogo' },
        
        // Crypto Payment Processors
        { pattern: /opennode\.com/i, name: 'OpenNode' },
        { pattern: /btcpayserver/i, name: 'BTCPay Server' },
        { pattern: /coinbase\.com\/commerce/i, name: 'Coinbase Commerce' },
        { pattern: /commerce\.coinbase\.com/i, name: 'Coinbase Commerce' },
        
        // Merchandise
        { pattern: /teespring\.com/i, name: 'Teespring' },
        { pattern: /redbubble\.com/i, name: 'Redbubble' },
        { pattern: /spreadshirt\.com/i, name: 'Spreadshirt' }
    ],
    
    // Cryptocurrency Wallet Addresses
    crypto: [
        // Bitcoin
        { pattern: /\b(bc1[a-zA-HJ-NP-Z0-9]{39,59})\b/, name: 'Bitcoin (bech32)' },
        { pattern: /\b([13][a-km-zA-HJ-NP-Z1-9]{25,34})\b/, name: 'Bitcoin (legacy)' },
        
        // Ethereum and EVM chains
        { pattern: /\b(0x[a-fA-F0-9]{40})\b/, name: 'Ethereum/EVM Address' },
        
        // Solana
        { pattern: /\b([1-9A-HJ-NP-Za-km-z]{32,44})\b/, name: 'Solana Address', requiresContext: true },
        
        // Monero
        { pattern: /\b(4[0-9AB][1-9A-HJ-NP-Za-km-z]{93})\b/, name: 'Monero Address' }
    ],
    
    // Widget Embeds
    widgets: [
        { pattern: /kofi-widget/i, name: 'Ko-fi Widget' },
        { pattern: /bmc-button/i, name: 'Buy Me a Coffee Button' },
        { pattern: /bmc-wbtn/i, name: 'Buy Me a Coffee Widget' },
        { pattern: /patreon-pledge-button/i, name: 'Patreon Button' },
        { pattern: /data-patreon-widget/i, name: 'Patreon Widget' },
        { pattern: /paypal-button/i, name: 'PayPal Button' },
        { pattern: /stripe-buy-button/i, name: 'Stripe Buy Button' }
    ],
    
    // Semantic Keywords Near Links
    semanticPatterns: [
        { 
            pattern: /<a[^>]*href=[^>]*>.*?\b(donate|donation|tip|sponsor|support\s*me|buy\s*me|fund|contribute)\b.*?<\/a>/gi, 
            name: 'Donation Link Text' 
        },
        { 
            pattern: /\b(donate|tip|sponsor)\b[^<]*<a/gi, 
            name: 'Donation Label Before Link' 
        }
    ],
    
    // FUNDING.yml indicators (when scanning repo)
    fundingYml: [
        { pattern: /\.github\/FUNDING\.yml/i, name: 'FUNDING.yml Present' },
        { pattern: /github:\s*\[?['"]?\w+/i, name: 'FUNDING.yml GitHub Entry' },
        { pattern: /patreon:\s*\w+/i, name: 'FUNDING.yml Patreon Entry' },
        { pattern: /ko_fi:\s*\w+/i, name: 'FUNDING.yml Ko-fi Entry' },
        { pattern: /open_collective:\s*\w+/i, name: 'FUNDING.yml Open Collective Entry' },
        { pattern: /custom:\s*\[/i, name: 'FUNDING.yml Custom Links' }
    ]
};

// Patterns that are ALLOWED (ecosystem contribution link)
const ALLOWED_PATTERNS = [
    /data-ecosystem-version/i,
    /puter-ecosystem-footer/i,
    /ecosystem-contribution-link/i,
    /\?ref=puter-ecosystem/i,
    /class=["'].*ecosystem.*["']/i
];

// ============================================================================
// DETECTION ENGINE
// ============================================================================

class ExternalContributionDetector {
    constructor(options = {}) {
        this.verbose = options.verbose || false;
        this.findings = [];
    }
    
    /**
     * Scan HTML content for external contribution methods
     * @param {string} content - HTML content to scan
     * @returns {object} Detection results
     */
    scan(content) {
        this.findings = [];
        
        // Check if content has ecosystem footer (required for compliance)
        const hasEcosystemFooter = ALLOWED_PATTERNS.some(p => p.test(content));
        
        // Scan for platforms
        for (const { pattern, name } of DETECTION_PATTERNS.platforms) {
            const matches = content.match(pattern);
            if (matches) {
                this.addFinding('platform', name, matches[0]);
            }
        }
        
        // Scan for crypto addresses
        for (const { pattern, name, requiresContext } of DETECTION_PATTERNS.crypto) {
            const matches = content.match(pattern);
            if (matches) {
                // For patterns that need context, check if it's in a donation context
                if (requiresContext) {
                    const contextPattern = new RegExp(
                        `(donate|wallet|address|crypto|bitcoin|eth|sol)[^<]{0,100}${matches[0]}|` +
                        `${matches[0]}[^<]{0,100}(donate|wallet|address|crypto)`,
                        'i'
                    );
                    if (contextPattern.test(content)) {
                        this.addFinding('crypto', name, matches[0]);
                    }
                } else {
                    this.addFinding('crypto', name, matches[0]);
                }
            }
        }
        
        // Scan for widgets
        for (const { pattern, name } of DETECTION_PATTERNS.widgets) {
            const matches = content.match(pattern);
            if (matches) {
                this.addFinding('widget', name, matches[0]);
            }
        }
        
        // Scan for semantic patterns
        for (const { pattern, name } of DETECTION_PATTERNS.semanticPatterns) {
            const matches = content.match(pattern);
            if (matches) {
                // Filter out ecosystem links
                const nonEcosystemMatches = matches.filter(m => 
                    !ALLOWED_PATTERNS.some(p => p.test(m))
                );
                if (nonEcosystemMatches.length > 0) {
                    this.addFinding('semantic', name, nonEcosystemMatches[0]);
                }
            }
        }
        
        return {
            compliant: this.findings.length === 0 && hasEcosystemFooter,
            hasEcosystemFooter,
            externalMethodsDetected: this.findings.length > 0,
            findings: this.findings,
            summary: this.getSummary()
        };
    }
    
    /**
     * Scan a FUNDING.yml file
     * @param {string} content - FUNDING.yml content
     * @returns {object} Detection results
     */
    scanFundingYml(content) {
        this.findings = [];
        
        for (const { pattern, name } of DETECTION_PATTERNS.fundingYml) {
            const matches = content.match(pattern);
            if (matches) {
                this.addFinding('funding_yml', name, matches[0]);
            }
        }
        
        return {
            compliant: this.findings.length === 0,
            hasFundingYml: true,
            findings: this.findings
        };
    }
    
    addFinding(category, method, match) {
        this.findings.push({
            category,
            method,
            match: match.substring(0, 100), // Truncate long matches
            timestamp: new Date().toISOString()
        });
        
        if (this.verbose) {
            console.log(`  [DETECTED] ${category}: ${method}`);
        }
    }
    
    getSummary() {
        if (this.findings.length === 0) {
            return 'No external contribution methods detected';
        }
        
        const methods = [...new Set(this.findings.map(f => f.method))];
        return `${this.findings.length} external method(s) detected: ${methods.join(', ')}`;
    }
}

// ============================================================================
// URL FETCHING
// ============================================================================

function fetchUrl(url) {
    return new Promise((resolve, reject) => {
        const parsedUrl = new URL(url);
        const protocol = parsedUrl.protocol === 'https:' ? https : http;
        
        const options = {
            hostname: parsedUrl.hostname,
            port: parsedUrl.port || (parsedUrl.protocol === 'https:' ? 443 : 80),
            path: parsedUrl.pathname + parsedUrl.search,
            method: 'GET',
            headers: {
                'User-Agent': 'Puter-Ecosystem-Compliance-Scanner/1.0'
            }
        };
        
        const req = protocol.request(options, (res) => {
            let data = '';
            res.on('data', chunk => data += chunk);
            res.on('end', () => resolve({ statusCode: res.statusCode, body: data }));
        });
        
        req.on('error', reject);
        req.setTimeout(10000, () => {
            req.destroy();
            reject(new Error('Request timeout'));
        });
        req.end();
    });
}

// ============================================================================
// CLI INTERFACE
// ============================================================================

async function main() {
    const args = process.argv.slice(2);
    
    if (args.length === 0) {
        console.log('Puter.js Ecosystem - External Contribution Detector');
        console.log('');
        console.log('Usage:');
        console.log('  node detect-external-contributions.js <url>');
        console.log('  node detect-external-contributions.js <url> --json');
        console.log('  node detect-external-contributions.js --scan-file <path>');
        console.log('');
        console.log('Examples:');
        console.log('  node detect-external-contributions.js https://user.github.io/app');
        console.log('  node detect-external-contributions.js ./index.html --scan-file');
        process.exit(0);
    }
    
    const jsonOutput = args.includes('--json');
    const scanFile = args.includes('--scan-file');
    const target = args.find(a => !a.startsWith('--'));
    
    if (!target) {
        console.error('Error: No target URL or file specified');
        process.exit(1);
    }
    
    const detector = new ExternalContributionDetector({ verbose: !jsonOutput });
    
    try {
        let content;
        
        if (scanFile) {
            const fs = require('fs');
            content = fs.readFileSync(target, 'utf-8');
        } else {
            if (!jsonOutput) console.log(`Scanning: ${target}`);
            const response = await fetchUrl(target);
            
            if (response.statusCode !== 200) {
                throw new Error(`HTTP ${response.statusCode}`);
            }
            
            content = response.body;
        }
        
        const results = detector.scan(content);
        
        if (jsonOutput) {
            console.log(JSON.stringify(results, null, 2));
        } else {
            console.log('');
            console.log('='.repeat(60));
            console.log('COMPLIANCE REPORT');
            console.log('='.repeat(60));
            console.log(`Ecosystem Footer: ${results.hasEcosystemFooter ? 'PRESENT' : 'MISSING'}`);
            console.log(`External Methods: ${results.externalMethodsDetected ? 'DETECTED' : 'NONE'}`);
            console.log(`Compliance Status: ${results.compliant ? 'COMPLIANT' : 'NON-COMPLIANT'}`);
            console.log('');
            console.log(`Summary: ${results.summary}`);
            
            if (results.findings.length > 0) {
                console.log('');
                console.log('Findings:');
                for (const finding of results.findings) {
                    console.log(`  - [${finding.category}] ${finding.method}`);
                }
            }
            console.log('='.repeat(60));
        }
        
        // Exit with appropriate code
        process.exit(results.compliant ? 0 : 1);
        
    } catch (error) {
        if (jsonOutput) {
            console.log(JSON.stringify({ error: error.message }));
        } else {
            console.error(`Error: ${error.message}`);
        }
        process.exit(2);
    }
}

// Export for use as module
module.exports = {
    ExternalContributionDetector,
    DETECTION_PATTERNS,
    ALLOWED_PATTERNS
};

// Run CLI if called directly
if (require.main === module) {
    main();
}

