codeBlocks.content:
/**
 * Puter.js Ecosystem Footer Styles
 * 
 * The exclusive contribution footer for ecosystem-compliant apps.
 * Keep this footer visible to remain eligible for ecosystem benefits.
 */

/* CSS Custom Properties */
.puter-ecosystem-footer {
    --eco-bg: #1a1a2e;
    --eco-bg-alt: #16213e;
    --eco-text: rgba(255, 255, 255, 0.7);
    --eco-text-hover: rgba(255, 255, 255, 0.95);
    --eco-accent: #6366f1;
    --eco-accent-hover: #818cf8;
    --eco-contribution: #f43f5e;
    --eco-contribution-hover: #fb7185;
    --eco-border: rgba(255, 255, 255, 0.08);
    --eco-font: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
}

/* Light mode variant */
@media (prefers-color-scheme: light) {
    .puter-ecosystem-footer:not([data-theme="dark"]) {
        --eco-bg: #f8fafc;
        --eco-bg-alt: #f1f5f9;
        --eco-text: rgba(0, 0, 0, 0.6);
        --eco-text-hover: rgba(0, 0, 0, 0.9);
        --eco-border: rgba(0, 0, 0, 0.08);
    }
}

/* Force dark theme */
.puter-ecosystem-footer[data-theme="dark"] {
    --eco-bg: #1a1a2e;
    --eco-bg-alt: #16213e;
    --eco-text: rgba(255, 255, 255, 0.7);
    --eco-text-hover: rgba(255, 255, 255, 0.95);
    --eco-border: rgba(255, 255, 255, 0.08);
}

/* Force light theme */
.puter-ecosystem-footer[data-theme="light"] {
    --eco-bg: #f8fafc;
    --eco-bg-alt: #f1f5f9;
    --eco-text: rgba(0, 0, 0, 0.6);
    --eco-text-hover: rgba(0, 0, 0, 0.9);
    --eco-border: rgba(0, 0, 0, 0.08);
}

/* Main Footer Container */
.puter-ecosystem-footer {
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    gap: 0.75rem;
    padding: 0.625rem 1rem;
    background: var(--eco-bg);
    border-top: 1px solid var(--eco-border);
    font-family: var(--eco-font);
    font-size: 0.75rem;
    line-height: 1.5;
    color: var(--eco-text);
}

/* Footer Links */
.puter-ecosystem-footer a {
    color: inherit;
    text-decoration: none;
    transition: color 0.15s ease;
}

.puter-ecosystem-footer a:hover {
    color: var(--eco-text-hover);
}

/* Separator */
.puter-ecosystem-footer .eco-sep {
    opacity: 0.3;
    user-select: none;
}

/* Built With Section */
.puter-ecosystem-footer .eco-built-with a {
    color: var(--eco-accent);
}

.puter-ecosystem-footer .eco-built-with a:hover {
    color: var(--eco-accent-hover);
}

/* Contribution Link - The ONLY permitted donation method */
.puter-ecosystem-footer .ecosystem-contribution-link {
    display: inline-flex;
    align-items: center;
    gap: 0.375rem;
    padding: 0.25rem 0.625rem;
    background: var(--eco-contribution);
    color: white !important;
    border-radius: 9999px;
    font-weight: 500;
    transition: background-color 0.15s ease, transform 0.15s ease;
}

.puter-ecosystem-footer .ecosystem-contribution-link:hover {
    background: var(--eco-contribution-hover);
    transform: translateY(-1px);
}

.puter-ecosystem-footer .ecosystem-contribution-link .eco-heart {
    display: inline-block;
    animation: eco-pulse 1.5s ease-in-out infinite;
}

@keyframes eco-pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.15); }
}

/* Directory Link */
.puter-ecosystem-footer .eco-directory-link {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.125rem 0.5rem;
    background: var(--eco-bg-alt);
    border-radius: 0.25rem;
    transition: background-color 0.15s ease;
}

.puter-ecosystem-footer .eco-directory-link:hover {
    background: var(--eco-border);
}

/* Minimized State */
.puter-ecosystem-footer.eco-minimized {
    padding: 0.375rem 1rem;
}

.puter-ecosystem-footer.eco-minimized .eco-full-view {
    display: none;
}

.puter-ecosystem-footer.eco-minimized .eco-mini-view {
    display: flex !important;
}

.puter-ecosystem-footer .eco-mini-view {
    display: none;
    align-items: center;
    gap: 0.5rem;
}

/* Controls */
.puter-ecosystem-footer .eco-controls {
    display: flex;
    gap: 0.125rem;
    margin-left: 0.5rem;
}

.puter-ecosystem-footer .eco-controls button {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 1.25rem;
    height: 1.25rem;
    padding: 0;
    background: transparent;
    border: none;
    border-radius: 0.25rem;
    color: var(--eco-text);
    cursor: pointer;
    opacity: 0.5;
    transition: opacity 0.15s ease, background-color 0.15s ease;
}

.puter-ecosystem-footer .eco-controls button:hover {
    opacity: 1;
    background: var(--eco-border);
}

/* Hide on very small screens */
@media (max-height: 300px) {
    .puter-ecosystem-footer {
        display: none;
    }
}

/* Responsive adjustments */
@media (max-width: 480px) {
    .puter-ecosystem-footer {
        padding: 0.5rem 0.75rem;
        font-size: 0.6875rem;
    }

    .puter-ecosystem-footer .eco-controls {
        display: none;
    }
}

/* Print styles - hide footer */
@media print {
    .puter-ecosystem-footer {
        display: none !important;
    }
}

