 * Ecosystem Node
 * 
 * Main orchestrator for the decentralized Puter.js ecosystem.
 * Coordinates IPFS storage, attestation chain, BitTorrent distribution,
 * and privacy-preserving operations.
 */

const { EcosystemIPFS } = require('./ipfs-storage');
const { AttestationChain, ScannerNode, AttestationType } = require('./attestation-chain');
const { EcosystemDistributor } = require('./torrent-distribution');
const { PrivacyClient, HiddenServiceManager } = require('./privacy-layer');
const { ExternalContributionDetector } = require('../scripts/detect-external-contributions');

// ============================================================================
// ECOSYSTEM NODE
// ============================================================================

class EcosystemNode {
    constructor(options = {}) {
        this.nodeId = options.nodeId || `node-${Date.now()}`;
        this.version = '2.0.0';
        
        // Initialize components
        this.ipfs = new EcosystemIPFS(options.ipfs || {});
        this.chain = new AttestationChain();
        this.scanner = new ScannerNode({
            nodeId: this.nodeId,
            version: this.version,
            chain: this.chain
        });
        this.distributor = new EcosystemDistributor(options.torrent || {});
        this.privacy = new PrivacyClient(options.privacy || {});
        this.hiddenServices = new HiddenServiceManager(options.privacy || {});
        
        // Detection engine
        this.detector = new ExternalContributionDetector({ verbose: false });
        
        // State
        this.registry = { apps: [], lastUpdated: null };
        this.isRunning = false;
        this.scanInterval = options.scanInterval || 86400000; // 24 hours
        this.scanTimer = null;
    }
    
    /**
     * Start the ecosystem node
     */
    async start() {
        console.log(`[${this.nodeId}] Starting ecosystem node...`);
        
        // Initialize privacy networks
        console.log(`[${this.nodeId}] Connecting to privacy networks...`);
        const privacyStatus = await this.privacy.initialize();
        console.log(`[${this.nodeId}] Privacy networks:`, privacyStatus);
        
        // Load existing chain from IPFS
        console.log(`[${this.nodeId}] Syncing attestation chain from IPFS...`);
        await this.syncFromIPFS();
        
        // Start periodic scanning
        this.isRunning = true;
        this.scheduleScan();
        
        console.log(`[${this.nodeId}] Ecosystem node started`);
        
        return this.getStatus();
    }
    
    /**
     * Stop the ecosystem node
     */
    async stop() {
        this.isRunning = false;
        if (this.scanTimer) {
            clearTimeout(this.scanTimer);
            this.scanTimer = null;
        }
        console.log(`[${this.nodeId}] Ecosystem node stopped`);
    }
    
    /**
     * Sync data from IPFS
     */
    async syncFromIPFS() {
        try {
            // Fetch registry
            const registry = await this.ipfs.getRegistry();
            if (registry) {
                this.registry = registry;
                console.log(`[${this.nodeId}] Loaded ${registry.apps.length} apps from registry`);
            }
            
            // Fetch compliance data
            const compliance = await this.ipfs.getCompliance();
            if (compliance && compliance.attestations) {
                // Replay attestations to rebuild chain
                for (const attestation of compliance.attestations) {
                    try {
                        this.chain.addAttestation(attestation);
                    } catch (e) {
                        console.warn(`[${this.nodeId}] Failed to add attestation:`, e.message);
                    }
                }
            }
        } catch (error) {
            console.warn(`[${this.nodeId}] IPFS sync failed:`, error.message);
        }
    }
    
    /**
     * Schedule next compliance scan
     */
    scheduleScan() {
        if (!this.isRunning) return;
        
        this.scanTimer = setTimeout(async () => {
            await this.runComplianceScan();
            this.scheduleScan();
        }, this.scanInterval);
    }
    
    /**
     * Run compliance scan on all registered apps
     */
    async runComplianceScan() {
        console.log(`[${this.nodeId}] Starting compliance scan...`);
        
        const results = [];
        const violations = [];
        const footerRemovals = [];
        
        for (const app of this.registry.apps) {
            try {
                const result = await this.scanApp(app.url);
                results.push(result);
                
                if (!result.compliant) {
                    if (result.optOutReason === 'FOOTER_REMOVED') {
                        footerRemovals.push({ url: app.url, result });
                    } else {
                        violations.push({ url: app.url, result });
                    }
                }
            } catch (error) {
                console.warn(`[${this.nodeId}] Scan failed for ${app.url}:`, error.message);
                results.push({
                    url: app.url,
                    compliant: null,
                    error: error.message
                });
            }
        }
        
        // Create attestations for violations
        for (const violation of footerRemovals) {
            const attestation = this.scanner.createFooterRemoval(violation.url, violation.result);
            this.scanner.submitAttestation(attestation);
        }
        
        for (const violation of violations) {
            const attestation = this.scanner.createExternalMethodDetection(
                violation.url,
                violation.result.findings
            );
            this.scanner.submitAttestation(attestation);
        }
        
        // Create bulk scan attestation
        const scanAttestation = this.scanner.createComplianceScan(results);
        this.scanner.submitAttestation(scanAttestation);
        
        // Publish results
        await this.publishResults();
        
        console.log(`[${this.nodeId}] Scan complete: ${results.length} apps, ${violations.length + footerRemovals.length} violations`);
        
        return {
            scanned: results.length,
            compliant: results.filter(r => r.compliant).length,
            violations: violations.length,
            footerRemovals: footerRemovals.length,
            errors: results.filter(r => r.error).length
        };
    }
    
    /**
     * Scan a single app for compliance
     */
    async scanApp(url) {
        // Fetch app content
        const response = await this.fetchWithPrivacy(url);
        
        if (!response.ok) {
            throw new Error(`HTTP ${response.status}`);
        }
        
        // Run detection
        const scanResult = this.detector.scan(response.body);
        
        return {
            url,
            ...scanResult,
            scannedAt: new Date().toISOString(),
            scannedBy: this.nodeId
        };
    }
    
    /**
     * Fetch URL with privacy fallback
     */
    async fetchWithPrivacy(url) {
        try {
            // Try direct fetch first
            const controller = new AbortController();
            const timeout = setTimeout(() => controller.abort(), 10000);
            
            const response = await fetch(url, { signal: controller.signal });
            clearTimeout(timeout);
            
            return {
                ok: response.ok,
                status: response.status,
                body: await response.text()
            };
        } catch (error) {
            // Fall back to privacy network
            console.log(`[${this.nodeId}] Direct fetch failed, trying privacy network...`);
            return this.privacy.fetch(url);
        }
    }
    
    /**
     * Publish results to decentralized networks
     */
    async publishResults() {
        const chainData = this.chain.export();
        
        // Publish to IPFS
        try {
            await this.ipfs.publishAttestation(chainData);
            console.log(`[${this.nodeId}] Published to IPFS`);
        } catch (error) {
            console.warn(`[${this.nodeId}] IPFS publish failed:`, error.message);
        }
        
        // Publish to BitTorrent
        try {
            const torrentResult = await this.distributor.publishChain(chainData);
            console.log(`[${this.nodeId}] Published to BitTorrent: ${torrentResult.magnetLink.substring(0, 50)}...`);
        } catch (error) {
            console.warn(`[${this.nodeId}] BitTorrent publish failed:`, error.message);
        }
    }
    
    /**
     * Register a new app
     */
    async registerApp(appInfo) {
        // Verify app is compliant before registering
        const scanResult = await this.scanApp(appInfo.url);
        
        if (!scanResult.compliant) {
            return {
                success: false,
                reason: scanResult.optOutReason,
                findings: scanResult.findings
            };
        }
        
        // Add to registry
        const app = {
            id: `app-${Date.now()}`,
            url: appInfo.url,
            repo: appInfo.repo,
            name: appInfo.name,
            category: appInfo.category || 'utilities',
            registered: new Date().toISOString(),
            stars: appInfo.stars || 0,
            compliant: true
        };
        
        this.registry.apps.push(app);
        this.registry.lastUpdated = new Date().toISOString();
        
        // Create registration attestation
        const attestation = this.scanner.createAppRegistration(app);
        this.scanner.submitAttestation(attestation);
        
        // Publish updated registry
        await this.ipfs.publishRegistry(this.registry);
        await this.distributor.publishRegistry(this.registry);
        
        return {
            success: true,
            app,
            attestationHash: attestation.hash
        };
    }
    
    /**
     * Get node status
     */
    getStatus() {
        return {
            nodeId: this.nodeId,
            version: this.version,
            isRunning: this.isRunning,
            chain: this.chain.getSummary(),
            registry: {
                apps: this.registry.apps.length,
                lastUpdated: this.registry.lastUpdated
            },
            privacy: this.privacy.getStatus(),
            distribution: this.distributor.getMagnetLinks()
        };
    }
    
    /**
     * Get magnet links for all ecosystem data
     */
    getMagnetLinks() {
        return this.distributor.getMagnetLinks();
    }
    
    /**
     * Get hidden service addresses
     */
    getHiddenServiceAddresses() {
        return this.hiddenServices.getAddresses();
    }
    
    /**
     * Deploy verifier as hidden services
     */
    async deployHiddenServices(port) {
        return this.hiddenServices.deployVerifier(port);
    }
    
    /**
     * Verify chain integrity
     */
    verifyChainIntegrity() {
        return this.chain.verifyChainIntegrity();
    }
    
    /**
     * Export all ecosystem data
     */
    exportData() {
        return {
            nodeId: this.nodeId,
            version: this.version,
            exportedAt: new Date().toISOString(),
            registry: this.registry,
            chain: this.chain.export(),
            magnetLinks: this.distributor.getMagnetLinks()
        };
    }
}

// ============================================================================
// CLI
// ============================================================================

async function main() {
    const args = process.argv.slice(2);
    const command = args[0];
    
    const node = new EcosystemNode({
        nodeId: process.env.NODE_ID || 'cli-node'
    });
    
    switch (command) {
        case 'start':
            await node.start();
            console.log('Node started. Press Ctrl+C to stop.');
            process.on('SIGINT', async () => {
                await node.stop();
                process.exit(0);
            });
            break;
            
        case 'scan':
            await node.start();
            const results = await node.runComplianceScan();
            console.log('Scan results:', results);
            await node.stop();
            break;
            
        case 'verify':
            const url = args[1];
            if (!url) {
                console.error('Usage: node ecosystem-node.js verify <url>');
                process.exit(1);
            }
            const result = await node.scanApp(url);
            console.log(JSON.stringify(result, null, 2));
            break;
            
        case 'status':
            await node.start();
            console.log(JSON.stringify(node.getStatus(), null, 2));
            await node.stop();
            break;
            
        case 'export':
            await node.start();
            const data = node.exportData();
            console.log(JSON.stringify(data, null, 2));
            await node.stop();
            break;
            
        default:
            console.log(`
Puter.js Ecosystem Node

Usage:
  node ecosystem-node.js <command> [args]

Commands:
  start           Start the ecosystem node
  scan            Run compliance scan on all registered apps
  verify <url>    Verify a single app
  status          Show node status
  export          Export all ecosystem data
            `);
    }
}

// ============================================================================
// EXPORTS
// ============================================================================

module.exports = { EcosystemNode };

// Run CLI if called directly
if (require.main === module) {
    main().catch(console.error);
}

"}

