        const ECOSYSTEM_CONFIG = {
            version: '2.0.0',
            templateRepo: 'https://github.com/ludoplex/puter-app-template',
            contributionUrl: 'https://github.com/sponsors/ludoplex',
            directoryUrl: 'https://ludoplex.github.io/puter-app-template/DIRECTORY.html',
            apiEndpoint: 'https://ludoplex.github.io/puter-app-template/ecosystem/directory.json',
            storageKey: 'puter-ecosystem-state'
        };
        
        function getEcosystemState() {
            try {
                const saved = localStorage.getItem(ECOSYSTEM_CONFIG.storageKey);
                return saved ? JSON.parse(saved) : { view: 'full' };
            } catch {
                return { view: 'full' };
            }
        }
        
        function setEcosystemState(state) {
            try {
                localStorage.setItem(ECOSYSTEM_CONFIG.storageKey, JSON.stringify(state));
            } catch {
                // Storage not available
            }
        }
        
        // Public API
        window.__puterEcosystem = {
            version: ECOSYSTEM_CONFIG.version,
            config: ECOSYSTEM_CONFIG,
            
            toggle: function() {
                const footer = document.querySelector('.puter-ecosystem-footer');
                if (!footer) return;
                
                footer.classList.toggle('eco-minimized');
                const isMinimized = footer.classList.contains('eco-minimized');
                setEcosystemState({ view: isMinimized ? 'mini' : 'full' });
            },
            
            hide: function() {
                const footer = document.querySelector('.puter-ecosystem-footer');
                if (footer) footer.style.display = 'none';
                setEcosystemState({ view: 'hidden' });
            },
            
            show: function() {
                const footer = document.querySelector('.puter-ecosystem-footer');
                if (footer) footer.style.display = '';
                setEcosystemState({ view: 'full' });
            },
            
            getAppId: function() {
                const footer = document.querySelector('.puter-ecosystem-footer');
                return footer ? footer.getAttribute('data-app-id') : null;
            },
            
            // Fetch ecosystem directory
            getDirectory: async function() {
                const response = await fetch(ECOSYSTEM_CONFIG.apiEndpoint);
                return response.json();
            },
            
            // Open another ecosystem app
            openApp: function(appUrl, params = {}) {
                const url = new URL(appUrl);
                url.searchParams.set('ref', this.getAppId());
                Object.entries(params).forEach(([k, v]) => url.searchParams.set(k, v));
                window.open(url.toString(), '_blank');
            }
        };
        
        // Restore saved state
        (function() {
            const state = getEcosystemState();
            const footer = document.querySelector('.puter-ecosystem-footer');
            
            if (footer) {
                if (state.view === 'hidden') {
                    footer.style.display = 'none';
                } else if (state.view === 'mini') {
                    footer.classList.add('eco-minimized');
                }
            }
            
            // Log ecosystem participation
            console.info(
                '%c Puter.js Ecosystem ',
                'background: #6366f1; color: white; font-weight: bold; padding: 2px 6px; border-radius: 3px;',
                '\n\nTemplate:', ECOSYSTEM_CONFIG.templateRepo,
                '\nDirectory:', ECOSYSTEM_CONFIG.directoryUrl,
                '\nVersion:', ECOSYSTEM_CONFIG.version
            );
        })();
        
        // ================================================================
        // APP FUNCTIONALITY
        // ================================================================
        
        const output = document.getElementById('output');
        
        function showOutput(message, type = 'default') {
            output.textContent = message;
            output.className = 'output show';
            if (type === 'success') output.classList.add('success');
            if (type === 'error') output.classList.add('error');
        }
        
        async function testPuter() {
            try {
                showOutput('Testing Puter.js connection...');
                
                const userInfo = await puter.auth.getUser();
                
                if (userInfo && userInfo.username) {
                    showOutput(
                        'Puter.js is working!\n\n' +
                        'User: ' + userInfo.username + '\n' +
                        'Status: Connected',
                        'success'
                    );
                } else {
                    showOutput(
                        'Puter.js loaded successfully!\n\n' +
                        'Tip: Sign in to access cloud features.',
                        'success'
                    );
                }
            } catch (error) {
                showOutput('Error: ' + error.message, 'error');
            }
        }
        
        async function testAI() {
            try {
                showOutput('Asking AI a question...');
                
                const response = await puter.ai.chat('Say hello in one short sentence!');
                
                showOutput('AI Response:\n\n' + response, 'success');
            } catch (error) {
                showOutput(
                    'Error: ' + error.message + '\n\n' +
                    'Note: AI features may require authentication.',
                    'error'
                );
            }
        }
        
        // Verify Puter.js loaded
        window.addEventListener('load', () => {
            if (typeof puter !== 'undefined') {
                console.log('Puter.js loaded successfully');
            } else {
                console.error('Puter.js failed to load');
            }
        });
    </script>
</body>
</html>

