        this.chain = options.chain || new AttestationChain();
        this.chain.registerScanner(this.nodeId, this.publicKey);
    }
    
    /**
     * Create a compliance scan attestation
     */
    createComplianceScan(results) {
        const builder = new AttestationBuilder({
            nodeId: this.nodeId,
            publicKey: this.publicKey,
            version: this.version
        });
        
        return builder
            .setType(AttestationType.COMPLIANCE_SCAN)
            .setSequence(this.chain.sequence + 1, this.chain.latestHash)
            .setData({
                scannedApps: results.length,
                compliant: results.filter(r => r.compliant).length,
                violations: results.filter(r => !r.compliant).length,
                results
            })
            .sign(this.privateKey)
            .build();
    }
    
    /**
     * Create an app registration attestation
     */
    createAppRegistration(appInfo) {
        const builder = new AttestationBuilder({
            nodeId: this.nodeId,
            publicKey: this.publicKey,
            version: this.version
        });
        
        return builder
            .setType(AttestationType.APP_REGISTRATION)
            .setSequence(this.chain.sequence + 1, this.chain.latestHash)
            .setData({
                url: appInfo.url,
                repo: appInfo.repo,
                name: appInfo.name,
                category: appInfo.category,
                stars: appInfo.stars || 0
            })
            .sign(this.privateKey)
            .build();
    }
    
    /**
     * Create a footer removal attestation (de facto opt-out)
     */
    createFooterRemoval(appUrl, scanResults) {
        const builder = new AttestationBuilder({
            nodeId: this.nodeId,
            publicKey: this.publicKey,
            version: this.version
        });
        
        return builder
            .setType(AttestationType.FOOTER_REMOVED)
            .setSequence(this.chain.sequence + 1, this.chain.latestHash)
            .setData({
                url: appUrl,
                reason: 'Ecosystem footer removed',
                scanResults
            })
            .sign(this.privateKey)
            .build();
    }
    
    /**
     * Create an external method detection attestation
     */
    createExternalMethodDetection(appUrl, findings) {
        const builder = new AttestationBuilder({
            nodeId: this.nodeId,
            publicKey: this.publicKey,
            version: this.version
        });
        
        return builder
            .setType(AttestationType.EXTERNAL_METHOD_DETECTED)
            .setSequence(this.chain.sequence + 1, this.chain.latestHash)
            .setData({
                url: appUrl,
                reason: 'External contribution method detected',
                findings
            })
            .sign(this.privateKey)
            .build();
    }
    
    /**
     * Submit attestation to chain
     */
    submitAttestation(attestation) {
        return this.chain.addAttestation(attestation);
    }
    
    /**
     * Get scanner info for registration
     */
    getInfo() {
        return {
            nodeId: this.nodeId,
            publicKey: this.publicKey,
            version: this.version
        };
    }
}

// ============================================================================
// EXPORTS
// ============================================================================

module.exports = {
    CHAIN_CONFIG,
    AttestationType,
    generateKeyPair,
    signData,
    verifySignature,
    hashData,
    AttestationBuilder,
    AttestationChain,
    ScannerNode
};

"}

