                    </div>
                    <div class="chain-stat">
                        <div class="chain-stat-label">Tracked Apps</div>
                        <div class="chain-stat-value" id="chain-apps">--</div>
                    </div>
                    <div class="chain-stat">
                        <div class="chain-stat-label">Compliant</div>
                        <div class="chain-stat-value" id="chain-compliant">--</div>
                    </div>
                    <div class="chain-stat">
                        <div class="chain-stat-label">Latest Hash</div>
                        <div class="chain-stat-value hash" id="chain-hash">--</div>
                    </div>
                </div>
            </div>
        </div>

        <!-- Network Panel -->
        <div class="panel">
            <div class="panel-header">
                <span class="panel-title">Decentralized Networks</span>
            </div>
            <div class="panel-content">
                <div class="networks">
                    <div class="network" id="net-ipfs">
                        <svg class="network-icon" viewBox="0 0 24 24" fill="currentColor">
                            <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
