        }
        
        .badge {
            padding: 0.25rem 0.5rem;
            border-radius: 0.25rem;
            font-size: 0.6875rem;
            font-weight: 600;
            text-transform: uppercase;
        }
        
        .badge.compliant {
            background: rgba(16, 185, 129, 0.2);
            color: var(--success);
        }
        
        .badge.violation {
            background: rgba(239, 68, 68, 0.2);
            color: var(--error);
        }
        
        .tabs {
            display: flex;
            gap: 0.5rem;
            margin-bottom: 1rem;
        }
        
        .tab {
            padding: 0.5rem 1rem;
            background: var(--surface-alt);
            border: 1px solid var(--border);
            border-radius: 0.375rem;
            color: var(--text-muted);
            font-size: 0.8125rem;
            cursor: pointer;
        }
        
        .tab.active {
            background: var(--accent);
            border-color: var(--accent);
            color: white;
        }
        
        .hidden { display: none !important; }
        
        /* Network Indicators */
        .networks {
            display: flex;
            gap: 0.75rem;
            flex-wrap: wrap;
        }
        
        .network {
            display: flex;
            align-items: center;
            gap: 0.375rem;
            padding: 0.375rem 0.75rem;
            background: var(--surface-alt);
            border-radius: 0.25rem;
            font-size: 0.6875rem;
        }
        
        .network-icon {
            width: 14px;
            height: 14px;
            opacity: 0.7;
        }
        
        footer {
            margin-top: 3rem;
            padding-top: 1.5rem;
            border-top: 1px solid var(--border);
            text-align: center;
            font-size: 0.75rem;
            color: var(--text-muted);
        }
        
        footer a {
            color: var(--accent);
            text-decoration: none;
        }
    </style>
</head>
<body>
    <div class="container">
        <header>
            <h1><span>[</span> Puter.js Ecosystem Verifier <span>]</span></h1>
            <p class="subtitle">Decentralized compliance verification for the Puter.js app ecosystem</p>
            
            <div class="status-bar">
                <div class="status-item">
                    <span class="status-dot" id="ipfs-status"></span>
                    <span>IPFS</span>
                </div>
                <div class="status-item">
                    <span class="status-dot" id="chain-status"></span>
                    <span>Attestation Chain</span>
                </div>
                <div class="status-item">
                    <span class="status-dot" id="puter-status"></span>
                    <span>Puter.js</span>
                </div>
            </div>
        </header>
        
        <!-- Verify Panel -->
        <div class="panel">
            <div class="panel-header">
                <span class="panel-title">Verify App Compliance</span>
            </div>
            <div class="panel-content">
                <div class="input-group">
                    <input type="url" id="verify-url" placeholder="https://username.github.io/app">
                    <button onclick="verifyApp()">Verify</button>
                </div>
                <div id="verify-results" class="results hidden"></div>
            </div>
        </div>
        
        <!-- Chain Status Panel -->
        <div class="panel">
            <div class="panel-header">
                <span class="panel-title">Attestation Chain Status</span>
                <button class="secondary" onclick="syncChain()">Sync</button>
            </div>
            <div class="panel-content">
                <div class="chain-info">
                    <div class="chain-stat">
                        <div class="chain-stat-label">Total Attestations</div>
                        <div class="chain-stat-value" id="chain-count">--</div>
                    </div>
                    <div class="chain-stat">
                        <div class="chain-stat-label">Tracked Apps</div>
                        <div class="chain-stat-value" id="chain-apps">--</div>
                    </div>
                    <div class="chain-stat">
                        <div class="chain-stat-label">Compliant</div>
                        <div class="chain-stat-value" id="chain-compliant">--</div>
                    </div>
                    <div class="chain-stat">
                        <div class="chain-stat-label">Latest Hash</div>
                        <div class="chain-stat-value hash" id="chain-hash">--</div>
                    </div>
                </div>
            </div>
        </div>
        
        <!-- Network Panel -->
        <div class="panel">
            <div class="panel-header">
                <span class="panel-title">Decentralized Networks</span>
            </div>
            <div class="panel-content">
                <div class="networks">
                    <div class="network" id="net-ipfs">
                        <svg class="network-icon" viewBox="0 0 24 24" fill="currentColor">
                            <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
                        </svg>
                        <span>IPFS Gateway</span>
                    </div>
                    <div class="network" id="net-torrent">
                        <svg class="network-icon" viewBox="0 0 24 24" fill="currentColor">
                            <circle cx="12" cy="12" r="10" stroke="currentColor" fill="none"/>
