/**
 * Puter.js Ecosystem Footer Styles
 * 
 * The exclusive contribution footer for ecosystem-compliant apps.
 * Keep this footer visible to remain eligible for ecosystem benefits.
 */

.puter-ecosystem-footer {
    --eco-bg: #1a1a2e;
    --eco-bg-alt: #16213e;
    --eco-text: rgba(255, 255, 255, 0.7);
    --eco-text-hover: rgba(255, 255, 255, 0.95);
    --eco-accent: #6366f1;
    --eco-accent-hover: #818cf8;
    --eco-contribution: #f43f5e;
    --eco-contribution-hover: #fb7185;
    --eco-border: rgba(255, 255, 255, 0.08);
    --eco-font: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
}

/* Light mode variant */
@media (prefers-color-scheme: light) {
    .puter-ecosystem-footer:not([data-theme="dark"]) {
        --eco-bg: #f8fafc;
        --eco-bg-alt: #f1f5f9;
        --eco-text: rgba(0, 0, 0, 0.6);
        --eco-text-hover: rgba(0, 0, 0, 0.9);
        --eco-border: rgba(0, 0, 0, 0.08);
    }
}

/* Force dark theme */
.puter-ecosystem-footer[data-theme="dark"] {
    --eco-bg: #1a1a2e;
    --eco-bg-alt: #16213e;
    --eco-text: rgba(255, 255, 255, 0.7);
    --eco-text-hover: rgba(255, 255, 255, 0.95);
    --eco-border: rgba(255, 255, 255, 0.08);
}

/* Force light theme */
.puter-ecosystem-footer[data-theme="light"] {
    --eco-bg: #f8fafc;
    --eco-bg-alt: #f1f5f9;
    --eco-text: rgba(0, 0, 0, 0.6);
    --eco-text-hover: rgba(0, 0, 0, 0.9);
    --eco-border: rgba(0, 0, 0, 0.08);
}

/* Main Footer Container */
.puter-ecosystem-footer {
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    gap: 0.75rem;
    padding: 0.625rem 1rem;
    background: var(--eco-bg);
    border-top: 1px solid var(--eco-border);
    font-family: var(--eco-font);
    font-size: 0.75rem;
    line-height: 1.5;
    color: var(--eco-text);
}

/* Footer Links */
.puter-ecosystem-footer a {
    color: inherit;
    text-decoration: none;
    transition: color 0.15s ease;
}

.puter-ecosystem-footer a:hover {
    color: var(--eco-text-hover);
}

/* Separator */
.puter-ecosystem-footer .eco-sep {
    opacity: 0.3;
    user-select: none;
}

/* Built With Section */
.puter-ecosystem-footer .eco-built-with a {
    color: var(--eco-accent);
}

.puter-ecosystem-footer .eco-built-with a:hover {
    color: var(--eco-accent-hover);
}

