.puter-ecosystem-footer .eco-directory-link {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.125rem 0.5rem;
    background: var(--eco-bg-alt);
    border-radius: 0.25rem;
    transition: background-color 0.15s ease;
}

.puter-ecosystem-footer .eco-directory-link:hover {
    background: var(--eco-border);
}

