{"contents":"# hooks/enforce-linting-v7.ps1
# PowerShell 7+ version - Enforce linting after file edits using Lint-Project scripts
# Universal Config Paradigm: Works with lint.cfg

[CmdletBinding()]
param()

$ErrorActionPreference = "Continue"

# Read JSON input from stdin
$jsonInput = $input | Out-String | ConvertFrom-Json

$filePath = $jsonInput.file_path
$workspaceRoots = $jsonInput.workspace_roots

if (-not $filePath) {
    exit 0
}

# Determine file type
$fileExt = [System.IO.Path]::GetExtension($filePath).TrimStart('.')
$shouldLint = $false
$lintType = ""

switch ($fileExt.ToLower()) {
    { $_ -in @('c', 'h', 'cpp', 'hpp', 'cc', 'hh', 'C', 'H') } {
        $shouldLint = $true
        $lintType = "c"
    }
    { $_ -in @('sh', 'bash', 'zsh') } {
        $shouldLint = $true
        $lintType = "shell"
    }
    { $_ -in @('ps1', 'psm1') } {
        $shouldLint = $true
        $lintType = "powershell"
    }
    { $_ -in @('json', 'yaml', 'yml', 'md') } {
        $shouldLint = $true
        $lintType = "config"
    }
}

if ($shouldLint) {
    # Find lint script using Search-Files if available
    $lintScript = $null
    
    if ($workspaceRoots -and $workspaceRoots.Count -gt 0) {
        $workspaceRoot = $workspaceRoots[0]
        
        # Try to find lint script using Search-Files.ps1
        $searchScriptPaths = @(
            Join-Path $workspaceRoot "github-backup\cursor-project-scaffolding-backup\utils\Search-Files.ps1"
            Join-Path $workspaceRoot "..\cursor-backup-store\github-backup\cursor-project-scaffolding-backup\utils\Search-Files.ps1"
            Join-Path $workspaceRoot "utils\Search-Files.ps1"
            Join-Path $workspaceRoot "..\utils\Search-Files.ps1"
        )
        
        $searchScript = $null
        foreach ($path in $searchScriptPaths) {
            if (Test-Path $path) {
                $searchScript = $path
                break
            }
        }
        
        if ($searchScript) {
            # Use Search-Files to find lint script
            try {
                $lintResults = & $searchScript -Pattern "Lint-Project*.ps1" -Path $workspaceRoot 2>$null | Select-Object -First 1
                if ($lintResults -and $lintResults.FullName) {
                    $lintScript = $lintResults.FullName
                }
            } catch {
                # Fallback if Search-Files fails
            }
        }
        
        # Fallback: direct path check
        if (-not $lintScript) {
            $possiblePaths = @(
                Join-Path $workspaceRoot "Lint-Project-v7.ps1"
                Join-Path $workspaceRoot "Lint-Project-v5.ps1"
                Join-Path $workspaceRoot "lint-project.sh"
                Join-Path $workspaceRoot "..\Lint-Project-v7.ps1"
                Join-Path $workspaceRoot "..\Lint-Project-v5.ps1"
                Join-Path $workspaceRoot "..\cursor-backup-store\Lint-Project-v7.ps1"
                Join-Path $workspaceRoot "..\cursor-backup-store\Lint-Project-v5.ps1"
            )
            
            foreach ($path in $possiblePaths) {
                if (Test-Path $path) {
                    $lintScript = $path
                    break
                }
            }
        }
        
        if ($lintScript) {
            # Run linting in background
            $logDir = Join-Path $env:USERPROFILE ".cursor\hooks\logs"
            New-Item -ItemType Directory -Path $logDir -Force | Out-Null
            $logFile = Join-Path $logDir "lint-$(Get-Date -Format 'yyyyMMdd-HHmmss').log"
            
            if ($lintScript -match '\.ps1$') {
                Start-Job -ScriptBlock {
                    param($script, $log)
                    & $script *>&1 | Out-File -FilePath $log -Encoding utf8
                } -ArgumentList $lintScript, $logFile | Out-Null
            } elseif ($lintScript -match '\.sh$') {
                Start-Process -FilePath "bash" -ArgumentList $lintScript -NoNewWindow -RedirectStandardOutput $logFile | Out-Null
            }
            
            $auditLog = Join-Path $logDir "lint-audit.log"
            $timestamp = Get-Date -Format 'yyyy-MM-dd HH:mm:ss'
            "[$timestamp] Linting triggered for $filePath (type: $lintType)" | Out-File -FilePath $auditLog -Append -Encoding utf8
        }
    }
}

exit 0

