{"command": "Push-Location "C:\Users\user\Downloads\Cursor_working_directory_misc\agent-services\mcp-orchestrator"

Write-Host "=== mcp-orchestrator Directory Structure ===" -ForegroundColor Cyan
Write-Host "Generated: $(Get-Date -Format 'yyyy-MM-dd HH:mm:ss')" -ForegroundColor Gray
Write-Host ""

function Show-Tree {
    param(
        [string]$Path = ".",
        [string]$Indent = "",
        [bool]$IsLast = $true
    )

    $item = Get-Item $Path
    $name = Split-Path $Path -Leaf
    if ($name -eq "") { $name = "mcp-orchestrator" }

    $prefix = if ($IsLast) { "`-- " } else { "|-- " }
    $nextIndent = if ($IsLast) { "   " } else { "|  " }

    $type = if ($item.PSIsContainer) { "[DIR]" } else { "[FILE]" }
    $size = if (-not $item.PSIsContainer) { " ($([math]::Round($item.Length/1KB, 2)) KB)" } else { "" }
    $date = $item.LastWriteTime.ToString("yyyy-MM-dd HH:mm:ss")

    Write-Host "$Indent$prefix$name$size" -ForegroundColor $(if ($item.PSIsContainer) { "Cyan" } else { "White" })
    Write-Host "$Indent$nextIndent  Modified: $date" -ForegroundColor Gray

    if ($item.PSIsContainer) {
        $children = Get-ChildItem -Path $Path -Force | Sort-Object Name
        for ($i = 0; $i -lt $children.Count; $i++) {
            $child = $children[$i]
            $childIsLast = $i -eq ($children.Count - 1)
            Show-Tree -Path $child.FullName -Indent "$Indent$nextIndent" -IsLast $childIsLast
        }
    }
}

Show-Tree -Path "."

Write-Host "`n=== Files Modified on 2025-12-25 ===" -ForegroundColor Yellow
$today = Get-Date -Date "2025-12-25"
$todayFiles = Get-ChildItem -Recurse -File | Where-Object { $_.LastWriteTime.Date -eq $today.Date } | Sort-Object FullName
if ($todayFiles) {
    foreach ($file in $todayFiles) {
        $relativePath = $file.FullName.Replace((Get-Location).Path + "\", "")
        Write-Host "$relativePath | $($file.LastWriteTime.ToString('HH:mm:ss')) | $([math]::Round($file.Length/1KB, 2)) KB" -ForegroundColor Green
    }
} else {
    Write-Host "No files modified on 2025-12-25" -ForegroundColor Gray
    Write-Host "`nMost recently modified files:" -ForegroundColor Cyan
    Get-ChildItem -Recurse -File | Sort-Object LastWriteTime -Descending | Select-Object -First 5 | ForEach-Object {
        $relativePath = $_.FullName.Replace((Get-Location).Path + "\", "")
        Write-Host "$relativePath | $($_.LastWriteTime.ToString('yyyy-MM-dd HH:mm:ss'))" -ForegroundColor White
    }
}

Pop-Location", "is_background": false, "explanation": "Show directory structure with file details and modification dates"}

