# The hex format is: [operation][context]
# Operations: 0-7
# Contexts: 8-9, A-F
# So '7A' means: Operation 7 (Generate/Codegen) + Context A (Web/Browser)

$correctTest = @'
import { decodeHex, OPERATIONS, CONTEXTS, getMcpServer } from './dist/hex-decoder.js';
import { orchestrate } from './dist/index.js';

async function test() {
  console.log('=== MCP Orchestrator Tests ===\n');

  // Test 1: Valid hex - 7A (Generate + Web/Browser = Browserbase)
  console.log('Test 1: Decode 7A (Generate/Codegen + Web/Browser)');
  const decoded1 = decodeHex('7A');
  if (decoded1) {
    console.log('Γ£à Success');
    console.log('   Operation: ' + OPERATIONS[decoded1.operation] + ' (' + decoded1.operation + ')');
    console.log('   Context: ' + CONTEXTS[decoded1.context] + ' (' + decoded1.context + ')');
    console.log('   MCP Server: ' + getMcpServer(decoded1.context));
  } else {
    console.log('Γ¥î Failed');
  }
  console.log('');

  // Test 2: Invalid hex
  console.log('Test 2: Invalid hex (A7)');
  const decoded2 = decodeHex('A7');
  if (!decoded2) {
    console.log('Γ£à Correctly rejected invalid hex');
  } else {
    console.log('Γ¥î Should have rejected');
  }
  console.log('');

  // Test 3: Orchestrate - Browserbase (7A)
  console.log('Test 3: Orchestrate Browserbase (7A)');
  const result1 = await orchestrate('7A', {
    url: 'https://example.com',
    extract: ['code', 'examples'],
  });
  console.log('Γ£à Result:');
  console.log('   MCP: ' + result1.mcp);
  console.log('   Success: ' + result1.success);
  if (result1.task) {
    console.log('   Task: ' + JSON.stringify(result1.task, null, 2));
  }
  console.log('');

  // Test 4: Orchestrate - Hugging Face (7B = Generate + Code/Templates)
  console.log('Test 4: Orchestrate Hugging Face (7B)');
  const result2 = await orchestrate('7B', {
    model: 'codellama/CodeLlama-7b-Instruct-hf',
    prompt: 'Write a hello world function',
  });
  console.log('Γ£à Result:');
  console.log('   MCP: ' + result2.mcp);
  console.log('   Success: ' + result2.success);
  if (result2.task) {
    console.log('   Task: ' + JSON.stringify(result2.task, null, 2));
  }
  console.log('');

  // Test 5: Orchestrate - Google Drive (1E = Read/Search + Storage/Drive)
  console.log('Test 5: Orchestrate Google Drive (1E)');
  const result3 = await orchestrate('1E', {
    query: 'test file',
  });
  console.log('Γ£à Result:');
  console.log('   MCP: ' + result3.mcp);
  console.log('   Success: ' + result3.success);
  if (result3.task) {
    console.log('   Task: ' + JSON.stringify(result3.task, null, 2));
  }
  console.log('');

  // Test 6: Orchestrate - Sentry (1F = Read/Search + Monitor/Sentry)
  console.log('Test 6: Orchestrate Sentry (1F)');
  const result4 = await orchestrate('1F', {
    org: 'my-org',
    query: 'critical errors',
  });
  console.log('Γ£à Result:');
  console.log('   MCP: ' + result4.mcp);
  console.log('   Success: ' + result4.success);
  if (result4.task) {
    console.log('   Task: ' + JSON.stringify(result4.task, null, 2));
  }
  console.log('');

  console.log('=== All Tests Complete ===');
}

test().catch(console.error);
'@

$correctTest | Out-File -FilePath "test-correct.mjs" -Encoding UTF8 -NoNewline

Write-Host "=== Running Corrected Tests ===" -ForegroundColor Cyan
node test-correct.mjs

