       import { HexCommand, HexContext } from '../hex-decoder'; // Added HexContext import

       export interface HFTask {
         operation: string;
         model?: string;
         prompt?: string;
         query?: string;
       }

       export function routeHuggingFace(command: HexCommand, args?: Record<string, any>): HFTask | null {
         const { operation, context } = command;

         // Removed invalid context check: if (context !== 'B' && context !== '7')
         if (context !== 'B') { // Corrected to only check for 'B'
           return null;
         }

         switch (operation) {
           case '1':
             return {
               operation: 'search',
               query: args?.query,
               // Removed 'task' property as it doesn't exist in HFTask interface
             };
           case '7':
