                           }
                       ],
                       "root": "gpt-3.5-turbo",
                       "parent": None
                   }
               ],
               "object": "list"
           })

       @app.route('/v1/chat/completions', methods=['POST'])
       def chat_completions():
           # Proxy chat completions, modifying model name in response
           try:
               data = request.get_json()

               # Modify the request payload if needed before sending to Qwen
               # For example, if Qwen expects a different model name internally
               # data['model'] = 'qwen-internal-name' 

               response = requests.post(f"{QWEN_SERVER_URL}/chat/completions", json=data)
               response_json = response.json()

               # Modify the model name in the response from Qwen
               if 'model' in response_json:
                   response_json['model'] = 'gpt-3.5-turbo'
               if 'choices' in response_json:
                   for choice in response_json['choices']:
                       if 'message' in choice and 'model' in choice['message']:
                           choice['message']['model'] = 'gpt-3.5-turbo'

               return jsonify(response_json), response.status_code
           except Exception as e:
               return jsonify({"error": str(e)}), 500

       @app.route('/v1/<path:path>', methods=['GET', 'POST', 'PUT', 'DELETE'])
       def proxy_all(path):
           # Generic proxy for all other /v1 requests
           try:
               full_url = f"{QWEN_SERVER_URL}/{path}"

               if request.method == 'GET':
                   resp = requests.get(full_url, params=request.args)
               elif request.method == 'POST':
                   resp = requests.post(full_url, json=request.get_json(), params=request.args)
               elif request.method == 'PUT':
                   resp = requests.put(full_url, json=request.get_json(), params=request.args)
               elif request.method == 'DELETE':
                   resp = requests.delete(full_url, params=request.args)
               else:
                   return jsonify({"error": "Method not allowed"}), 405

               # Recreate the response to ensure all headers and content are passed through
               excluded_headers = ['content-encoding', 'content-length', 'transfer-encoding', 'connection']
               headers = [(name, value) for name, value in resp.raw.headers.items() if name.lower() not in excluded_headers]

               return Response(resp.content, resp.status_code, headers)
           except requests.exceptions.RequestException as e:
               return jsonify({"error": f"Proxy request failed: {e}"}), 500
           except Exception as e:
               return jsonify({"error": f"An unexpected error occurred: {e}"}), 500

       if __name__ == '__main__':
           app.run(host='0.0.0.0', port=8081)
       ```
