{"contents":"export const OPERATIONS = {
    '0': 'Create/Scaffold',
    '1': 'Read/Search',
    '2': 'Update/Modify',
    '3': 'Delete/Remove',
    '4': 'Execute/Run',
    '5': 'Parse/Validate',
    '6': 'Sync/Backup',
    '7': 'Generate/Codegen',
};
export const CONTEXTS = {
    '8': 'Filesystem',
    '9': 'Git/SVN',
    'A': 'Web/Browser',
    'B': 'Code/Templates',
    'C': 'MCP/API',
    'D': 'Parse/Feedback',
    'E': 'Storage/Drive',
    'F': 'Monitor/Sentry',
};
export function decodeHex(hex) {
    if (hex.length !== 2)
        return null;
    const operation = hex[0];
    const context = hex[1];
    if (!OPERATIONS[operation] || !CONTEXTS[context]) {
        return null;
    }
    return { operation, context, hex };
}
export function encodeHex(operation, context) {
    return `${operation}${context}`;
}
export function getMcpServer(context) {
    const serverMap = {
        '8': 'Google Drive',
        '9': 'Google Drive',
        'A': 'Browserbase',
        'B': 'Context7',
        'C': 'Apify',
        'D': 'Sentry',
        'E': 'Google Drive',
        'F': 'Sentry',
    };
    return serverMap[context] || null;
}
