{"command": "Write-Host "=== Searching Filesystem for Qwen Model ===" -ForegroundColor Cyan

# Search Windows drives more thoroughly
Write-Host "`nSearching Windows drives for large llamafile files..." -ForegroundColor Yellow

$drives = Get-PSDrive -PSProvider FileSystem | Where-Object { $_.Used -gt 0 } | Select-Object -ExpandProperty Name

foreach ($drive in $drives) {
    $drivePath = "${drive}:\"
    Write-Host "`nSearching $drivePath..." -ForegroundColor Cyan

    # Search for llamafile files, especially large ones
    try {
        $files = Get-ChildItem -Path $drivePath -Recurse -Filter "*.llamafile" -ErrorAction SilentlyContinue | 
            Where-Object { $_.Length -gt 1GB } | 
            Select-Object FullName, @{N="Size(GB)";E={[math]::Round($_.Length/1GB,2)}}, LastWriteTime -First 5

        if ($files) {
            Write-Host "  Γ£à Found large llamafile files:" -ForegroundColor Green
            $files | ForEach-Object {
                Write-Host "    $($_.FullName)" -ForegroundColor White
                Write-Host "      Size: $($_.'Size(GB)') GB | Modified: $($_.LastWriteTime)" -ForegroundColor Gray
            }
        }
    } catch {
        Write-Host "  ΓÜá∩╕Å Error searching $drivePath : $_" -ForegroundColor Yellow
    }
}

# Also search for files with "qwen" in name that are large
Write-Host "`n=== Searching for large files with 'qwen' in name ===" -ForegroundColor Cyan
foreach ($drive in $drives) {
    $drivePath = "${drive}:\"
    try {
        $qwenFiles = Get-ChildItem -Path $drivePath -Recurse -Filter "*qwen*" -ErrorAction SilentlyContinue | 
            Where-Object { $_.Length -gt 1GB -and -not $_.PSIsContainer } | 
            Select-Object FullName, @{N="Size(GB)";E={[math]::Round($_.Length/1GB,2)}}, LastWriteTime -First 3

        if ($qwenFiles) {
            Write-Host "`nFound in $drivePath :" -ForegroundColor Green
            $qwenFiles | ForEach-Object {
                Write-Host "  $($_.FullName)" -ForegroundColor White
                Write-Host "    Size: $($_.'Size(GB)') GB | Modified: $($_.LastWriteTime)" -ForegroundColor Gray
            }
        }
    } catch {
        # Skip errors
    }
}", "is_background": false, "explanation": "Search all Windows drives thoroughly for the Qwen model file"}

