Write-Host "=== mcp-orchestrator Directory Structure ===" -ForegroundColor Cyan
Write-Host "Generated: $(Get-Date -Format 'yyyy-MM-dd HH:mm:ss')" -ForegroundColor Gray
Write-Host ""

# Get all files and directories recursively
function Get-DirectoryTree {
    param(
        [string]$Path = ".",
        [string]$Prefix = "",
        [int]$Depth = 0,
        [int]$MaxDepth = 10
    )

    if ($Depth -gt $MaxDepth) { return }

    $items = Get-ChildItem -Path $Path -Force | Sort-Object Name

    for ($i = 0; $i -lt $items.Count; $i++) {
        $item = $items[$i]
        $isLast = $i -eq ($items.Count - 1)
        $connector = if ($isLast) { "ΓööΓöÇΓöÇ " } else { "Γö£ΓöÇΓöÇ " }
        $nextPrefix = if ($isLast) { "    " } else { "Γöé   " }

        # Get file info
        $size = if ($item.PSIsContainer) { "" } else { " ($([math]::Round($item.Length/1KB, 2)) KB)" }
        $modified = $item.LastWriteTime.ToString("yyyy-MM-dd HH:mm:ss")

        Write-Host "$Prefix$connector$($item.Name)$size" -ForegroundColor $(if ($item.PSIsContainer) { "Cyan" } else { "White" })

        if ($item.PSIsContainer) {
            Get-DirectoryTree -Path $item.FullName -Prefix "$Prefix$nextPrefix" -Depth ($Depth + 1) -MaxDepth $MaxDepth
        }
    }
}

Get-DirectoryTree -Path "."

# Also show file details
Write-Host "`n=== File Details ===" -ForegroundColor Cyan
Get-ChildItem -Recurse -File | ForEach-Object {
    $relativePath = $_.FullName.Replace((Get-Location).Path + "\", "")
    Write-Host "$relativePath | $([math]::Round($_.Length, 0)) bytes | $($_.LastWriteTime.ToString('yyyy-MM-dd HH:mm:ss'))" -ForegroundColor Gray
}

