{"command": "Write-Host "=== Checking Cursor Settings for Model Configuration ===" -ForegroundColor Cyan

# Check Cursor settings for model configuration
$cursorSettings = "$env:APPDATA\Cursor\User\settings.json"
if (Test-Path $cursorSettings) {
    Write-Host "`nCursor Settings:" -ForegroundColor Yellow
    $settings = Get-Content $cursorSettings | ConvertFrom-Json

    # Check for model-related settings
    $modelSettings = $settings | ConvertTo-Json -Depth 10 | Select-String -Pattern "model|gpt|turbo|claude|anthropic" -CaseSensitive:$false
    if ($modelSettings) {
        Write-Host $modelSettings
    } else {
        Write-Host "  No model settings found in settings.json" -ForegroundColor Gray
    }
}

# Check for any model override or workaround files
Write-Host "`n=== Searching for Model Workaround Files ===" -ForegroundColor Cyan
$workaroundFiles = Get-ChildItem -Path "C:\Users\user" -Recurse -Filter "*model*" -Include "*.json","*.config","*.cfg" -ErrorAction SilentlyContinue | 
    Where-Object { $_.FullName -match "cursor|gpt|turbo" } | 
    Select-Object -First 5 FullName

if ($workaroundFiles) {
    Write-Host "Found potential config files:" -ForegroundColor Green
    $workaroundFiles | ForEach-Object { Write-Host "  $($_.FullName)" -ForegroundColor White }
} else {
    Write-Host "  No model workaround files found" -ForegroundColor Gray
}

Write-Host "`nΓÜá∩╕Å Could you clarify what you mean by 'GPT-3 Turbo workaround hiding it'?" -ForegroundColor Yellow
Write-Host "  - Hide model name in API calls?" -ForegroundColor White
Write-Host "  - Workaround for GPT-3.5 Turbo limitations?" -ForegroundColor White
Write-Host "  - Configure to use GPT-3.5 Turbo instead of another model?" -ForegroundColor White
Write-Host "  - Something else?" -ForegroundColor White", "is_background": false, "explanation": "Check Cursor settings and search for model workaround configurations"}

