{"command": "Write-Host "=== Searching WSL2 Filesystem for Qwen Model ===" -ForegroundColor Cyan

# Search in WSL2's native ext4 filesystem (not Windows mounts)
Write-Host "`nSearching WSL2 home directory..." -ForegroundColor Yellow
$wslSearch1 = wsl -e bash -c "find ~ -name '*qwen*coder*' -o -name '*.llamafile' 2>/dev/null | head -10" 2>&1
if ($wslSearch1 -and $wslSearch1 -notmatch "No such file") {
    Write-Host $wslSearch1
} else {
    Write-Host "  Not found in home directory" -ForegroundColor Gray
}

Write-Host "`nSearching /tmp..." -ForegroundColor Yellow
$wslSearch2 = wsl -e bash -c "find /tmp -name '*qwen*' -o -name '*.llamafile' 2>/dev/null | head -10" 2>&1
if ($wslSearch2 -and $wslSearch2 -notmatch "No such file") {
    Write-Host $wslSearch2
} else {
    Write-Host "  Not found in /tmp" -ForegroundColor Gray
}

Write-Host "`nSearching /opt..." -ForegroundColor Yellow
$wslSearch3 = wsl -e bash -c "find /opt -name '*qwen*' -o -name '*.llamafile' 2>/dev/null | head -10" 2>&1
if ($wslSearch3 -and $wslSearch3 -notmatch "No such file") {
    Write-Host $wslSearch3
} else {
    Write-Host "  Not found in /opt" -ForegroundColor Gray
}

Write-Host "`nSearching entire WSL2 filesystem (this may take a moment)..." -ForegroundColor Yellow
$wslSearch4 = wsl -e bash -c "find / -name '*qwen2.5-coder*' -o -name '*qwen*coder*.llamafile' 2>/dev/null | grep -v '/proc\|/sys\|/dev\|/mnt' | head -20" 2>&1
if ($wslSearch4 -and $wslSearch4 -notmatch "No such file" -and $wslSearch4.Length -gt 5) {
    Write-Host $wslSearch4
} else {
    Write-Host "  Not found in WSL2 filesystem" -ForegroundColor Gray
}", "is_background": false, "explanation": "Search WSL2 ext4 filesystem for the model file"}

