{"contents":"import { decodeHex, getMcpServer, HexCommand } from './hex-decoder';
import { routeApify, executeApifyTask } from './mcp-routers/apify-router';
import { routeGDrive, executeGDriveTask } from './mcp-routers/gdrive-router';
import { routeHuggingFace, executeHFTask } from './mcp-routers/huggingface-router';
import { routeContext7, executeContext7Task } from './mcp-routers/context7-router';
import { routeSentry, executeSentryTask } from './mcp-routers/sentry-router';
import { routeBrowserbase, executeBrowserbaseTask } from './mcp-routers/browserbase-router';

export interface OrchestrationResult {
  mcp: string;
  task: any;
  hex: string;
  success: boolean;
  message?: string;
}

export async function orchestrate(hex: string, args?: Record<string, any>): Promise<OrchestrationResult> {
  const decoded = decodeHex(hex);
  if (!decoded) {
    return {
      mcp: 'unknown',
      task: null,
      hex,
      success: false,
      message: 'Invalid hex code',
    };
  }
  
  const mcpServer = getMcpServer(decoded.context);
  if (!mcpServer) {
    return {
      mcp: 'unknown',
      task: null,
      hex,
      success: false,
      message: 'No MCP server for context',
    };
  }
  
  let task: any = null;
  let executor: (task: any) => Promise<any>;
  
  switch (mcpServer) {
    case 'Apify':
      task = routeApify(decoded, args);
      executor = executeApifyTask;
      break;
    case 'Google Drive':
      task = routeGDrive(decoded, args);
      executor = executeGDriveTask;
      break;
    case 'Hugging Face':
      task = routeHuggingFace(decoded, args);
      executor = executeHFTask;
      break;
    case 'Context7':
      task = routeContext7(decoded, args);
      executor = executeContext7Task;
      break;
    case 'Sentry':
      task = routeSentry(decoded, args);
      executor = executeSentryTask;
      break;
    case 'Browserbase':
      task = routeBrowserbase(decoded, args);
      executor = executeBrowserbaseTask;
      break;
    default:
      return {
        mcp: mcpServer,
        task: null,
        hex,
        success: false,
        message: 'Unsupported MCP server',
      };
  }
  
  if (!task) {
    return {
      mcp: mcpServer,
      task: null,
      hex,
      success: false,
      message: 'No task for operation',
    };
  }
  
  try {
    const result = await executor(task);
    return {
      mcp: mcpServer,
      task: result,
      hex,
      success: true,
    };
  } catch (error: any) {
    return {
      mcp: mcpServer,
      task: null,
      hex,
      success: false,
      message: error.message || 'Execution failed',
    };
  }
}

export { decodeHex, getMcpServer, OPERATIONS, CONTEXTS } from './hex-decoder';

