# Alternative: Use tree command if available, or Get-ChildItem with better formatting
Write-Host "=== mcp-orchestrator Structure (Alternative View) ===" -ForegroundColor Cyan
Write-Host ""

# Get all items recursively
$allItems = Get-ChildItem -Recurse -Force | Sort-Object FullName

# Group by directory
$structure = @{}
foreach ($item in $allItems) {
    $dir = Split-Path $item.FullName -Parent
    $dir = $dir.Replace((Get-Location).Path + "\", "")
    if (-not $structure[$dir]) {
        $structure[$dir] = @()
    }
    $name = Split-Path $item.FullName -Leaf
    $structure[$dir] += @{
        Name = $name
        IsDirectory = $item.PSIsContainer
        Size = if (-not $item.PSIsContainer) { $item.Length } else { 0 }
        Modified = $item.LastWriteTime
    }
}

# Display structure
foreach ($dir in ($structure.Keys | Sort-Object)) {
    if ($dir -eq "") { $dir = "." }
    Write-Host "$dir/" -ForegroundColor Cyan
    foreach ($item in ($structure[$dir] | Sort-Object Name)) {
        $icon = if ($item.IsDirectory) { "≡ƒôü" } else { "≡ƒôä" }
        $size = if ($item.Size -gt 0) { " ($([math]::Round($item.Size/1KB, 2)) KB)" } else { "" }
        Write-Host "  $icon $($item.Name)$size" -ForegroundColor White
    }
    Write-Host ""
}

# Show files modified today (12/25/2025)
Write-Host "=== Files Modified Today (2025-12-25) ===" -ForegroundColor Yellow
$today = Get-Date -Date "2025-12-25"
$todayFiles = Get-ChildItem -Recurse -File | Where-Object { $_.LastWriteTime.Date -eq $today.Date }
if ($todayFiles) {
    foreach ($file in $todayFiles) {
        $relativePath = $file.FullName.Replace((Get-Location).Path + "\", "")
        Write-Host "$relativePath | $($file.LastWriteTime.ToString('HH:mm:ss'))" -ForegroundColor Green
    }
} else {
    Write-Host "No files modified on 2025-12-25" -ForegroundColor Gray
}

Pop-Location", "is_background": false, "explanation": "Show directory structure and files modified today"}

