#!/usr/bin/env node
/**
 * Test script for mcp-orchestrator
 * Tests hex decoding, routing, and orchestration
 */

import { orchestrate, decodeHex, OPERATIONS, CONTEXTS } from './src/index.js';

async function runTests() {
  console.log('=== MCP Orchestrator Tests ===\n');

  // Test 1: Hex Decoding
  console.log('Test 1: Hex Decoding');
  const testHex = 'A7';
  const decoded = decodeHex(testHex);
  if (decoded) {
    console.log(`Γ£à Decoded "${testHex}":`);
    console.log(`   Operation: ${OPERATIONS[decoded.operation]} (${decoded.operation})`);
    console.log(`   Context: ${CONTEXTS[decoded.context]} (${decoded.context})`);
  } else {
    console.log(`Γ¥î Failed to decode "${testHex}"`);
  }
  console.log('');

  // Test 2: Invalid Hex
  console.log('Test 2: Invalid Hex Code');
  const invalidResult = await orchestrate('ZZ', {});
  console.log(`Γ£à Invalid hex handled: ${invalidResult.success ? 'Γ¥î' : 'Γ£à'} (expected false)`);
  console.log(`   Message: ${invalidResult.message}`);
  console.log('');

  // Test 3: Valid Orchestration - Browserbase (A7 = Generate/Codegen + Web/Browser)
  console.log('Test 3: Browserbase Orchestration (A7)');
  const browserbaseResult = await orchestrate('A7', {
    url: 'https://example.com',
    extract: ['code', 'examples'],
  });
  console.log(`Γ£à Orchestration result:`);
  console.log(`   MCP: ${browserbaseResult.mcp}`);
  console.log(`   Success: ${browserbaseResult.success}`);
  console.log(`   Task: ${JSON.stringify(browserbaseResult.task, null, 2)}`);
  console.log('');

  // Test 4: Hugging Face (B7 = Generate/Codegen + Code/Templates)
  console.log('Test 4: Hugging Face Orchestration (B7)');
  const hfResult = await orchestrate('B7', {
    model: 'codellama/CodeLlama-7b-Instruct-hf',
    prompt: 'Write a hello world function',
  });
  console.log(`Γ£à Orchestration result:`);
  console.log(`   MCP: ${hfResult.mcp}`);
  console.log(`   Success: ${hfResult.success}`);
  console.log(`   Task: ${JSON.stringify(hfResult.task, null, 2)}`);
  console.log('');

  // Test 5: Google Drive (E1 = Read/Search + Storage/Drive)
  console.log('Test 5: Google Drive Orchestration (E1)');
  const gdriveResult = await orchestrate('E1', {
    query: 'test file',
  });
  console.log(`Γ£à Orchestration result:`);
  console.log(`   MCP: ${gdriveResult.mcp}`);
  console.log(`   Success: ${gdriveResult.success}`);
  console.log(`   Task: ${JSON.stringify(gdriveResult.task, null, 2)}`);
  console.log('');

  // Test 6: Sentry (F1 = Read/Search + Monitor/Sentry)
  console.log('Test 6: Sentry Orchestration (F1)');
  const sentryResult = await orchestrate('F1', {
    org: 'my-org',
    query: 'critical errors',
  });
  console.log(`Γ£à Orchestration result:`);
  console.log(`   MCP: ${sentryResult.mcp}`);
  console.log(`   Success: ${sentryResult.success}`);
  console.log(`   Task: ${JSON.stringify(sentryResult.task, null, 2)}`);
  console.log('');

  // Test 7: Context7 (B1 = Read/Search + Code/Templates)
  console.log('Test 7: Context7 Orchestration (B1)');
  const context7Result = await orchestrate('B1', {
    query: 'TypeScript types',
  });
  console.log(`Γ£à Orchestration result:`);
  console.log(`   MCP: ${context7Result.mcp}`);
  console.log(`   Success: ${context7Result.success}`);
  console.log(`   Task: ${JSON.stringify(context7Result.task, null, 2)}`);
  console.log('');

  // Test 8: Apify (C1 = Read/Search + MCP/API)
  console.log('Test 8: Apify Orchestration (C1)');
  const apifyResult = await orchestrate('C1', {
    query: 'web scraping',
  });
  console.log(`Γ£à Orchestration result:`);
  console.log(`   MCP: ${apifyResult.mcp}`);
  console.log(`   Success: ${apifyResult.success}`);
  console.log(`   Task: ${JSON.stringify(apifyResult.task, null, 2)}`);
  console.log('');

  console.log('=== All Tests Complete ===');
}

runTests().catch(console.error);

