{"command": "Push-Location "C:\Users\user\Downloads\Cursor_working_directory_misc\agent-services"

# Check each TypeScript project and lint them
$projects = @(
    "mcp-orchestrator",
    "auto-parse",
    "char-commands",
    "workflows"
)

foreach ($project in $projects) {
    $pkgFile = Join-Path $project "package.json"
    if (Test-Path $pkgFile) {
        Write-Host "`n=== Linting $project ===" -ForegroundColor Yellow
        Push-Location $project

        # Check if node_modules exists, if not install
        if (-not (Test-Path "node_modules")) {
            Write-Host "Installing dependencies..." -ForegroundColor Cyan
            npm install 2>&1 | Out-Null
        }

        # Try to run lint/check/build scripts
        $pkg = Get-Content "package.json" | ConvertFrom-Json
        if ($pkg.scripts) {
            # Try lint first, then check, then build
            $scripts = @("lint", "check", "build", "type-check")
            $ran = $false

            foreach ($scriptName in $scripts) {
                if ($pkg.scripts.$scriptName) {
                    Write-Host "Running: npm run $scriptName" -ForegroundColor Cyan
                    npm run $scriptName 2>&1
                    $ran = $true
                    break
                }
            }

            if (-not $ran) {
                # Fallback: use tsc directly if tsconfig.json exists
                if (Test-Path "tsconfig.json") {
                    Write-Host "Running: npx tsc --noEmit" -ForegroundColor Cyan
                    npx tsc --noEmit 2>&1
                } else {
                    Write-Host "ΓÜá∩╕Å No lint scripts or tsconfig.json found" -ForegroundColor Yellow
                }
            }
        } else {
            # No scripts, try tsc directly
            if (Test-Path "tsconfig.json") {
                Write-Host "Running: npx tsc --noEmit" -ForegroundColor Cyan
                npx tsc --noEmit 2>&1
            }
        }

        Pop-Location
    }
}

