{"command": "# Check if there's a models directory or cache
Write-Host "`n=== Checking Hugging Face Cache ===" -ForegroundColor Cyan

$hfCache = wsl -e bash -c "find ~/.cache -name '*qwen*' -type f 2>/dev/null | head -10" 2>&1
if ($hfCache -and $hfCache.Length -gt 2) {
    Write-Host "Found in Hugging Face cache:" -ForegroundColor Green
    Write-Host $hfCache

    # Get file sizes
    foreach ($file in ($hfCache -split "`n")) {
        if ($file -and $file.Length -gt 1) {
            $file = $file.Trim()
            $size = wsl -e bash -c "ls -lh '$file' 2>/dev/null | awk '{print \$5, \$9}'" 2>&1
            Write-Host "  $size" -ForegroundColor White
        }
    }
} else {
    Write-Host "  Not found in ~/.cache" -ForegroundColor Gray
}

# Check for the exact filename
Write-Host "`n=== Searching for exact filename ===" -ForegroundColor Cyan
$exactFile = wsl -e bash -c "find / -name 'qwen2.5-coder-7b-instruct.Q4_K_M.llamafile' 2>/dev/null | grep -v '/proc\|/sys\|/dev\|/mnt' | head -5" 2>&1
if ($exactFile -and $exactFile.Length -gt 2) {
    Write-Host "Γ£à Found exact file:" -ForegroundColor Green
    Write-Host $exactFile

    # Get full details
    foreach ($file in ($exactFile -split "`n")) {
        if ($file -and $file.Length -gt 1) {
            $file = $file.Trim()
            $info = wsl -e bash -c "ls -lh '$file' && file '$file' 2>/dev/null" 2>&1
            Write-Host "`n  $info" -ForegroundColor White
        }
    }
} else {
    Write-Host "  Exact filename not found" -ForegroundColor Gray
}", "is_background": false, "explanation": "Check Hugging Face cache and search for exact filename"}

