toolFormerData.rawArgs:
{"command": "Write-Host "=== Searching Windows Filesystem for Large Model Files ===" -ForegroundColor Cyan

# Search for large files that might be the model (4-5GB range)
Write-Host "`nSearching for large .llamafile files (4-5GB)..." -ForegroundColor Yellow

# Check common locations on Windows
$windowsLocations = @(
    "C:\Users\user\Downloads",
    "C:\Users\user\Documents",
    "D:\",
    "C:\Users\user\AI-Models",
    "C:\Users\user\AppData\Local",
    "C:\Users\user\AppData\Roaming"
)

foreach ($loc in $windowsLocations) {
    if (Test-Path $loc) {
        Write-Host "`nChecking: $loc" -ForegroundColor Yellow
        try {
            $files = Get-ChildItem -Path $loc -Recurse -Filter "*.llamafile" -ErrorAction SilentlyContinue | 
                Where-Object { $_.Length -gt 1GB } | 
                Select-Object FullName, @{N="Size(GB)";E={[math]::Round($_.Length/1GB,2)}} -First 5

            if ($files) {
                Write-Host "  Γ£à Found large llamafile files:" -ForegroundColor Green
                $files | ForEach-Object {
                    Write-Host "    $($_.FullName) - $($_.'Size(GB)') GB" -ForegroundColor White
                }
            }
        } catch {
            Write-Host "  ΓÜá∩╕Å Error searching: $_" -ForegroundColor Yellow
        }
    }
}

# Also search for files with "qwen" in the name
Write-Host "`n=== Searching for files with 'qwen' in name ===" -ForegroundColor Cyan
$qwenFiles = Get-ChildItem -Path "C:\Users\user" -Recurse -Filter "*qwen*" -ErrorAction SilentlyContinue | 
    Select-Object FullName, @{N="Size(MB)";E={[math]::Round($_.Length/1MB,2)}} -First 10

if ($qwenFiles) {
    Write-Host "Found files with 'qwen' in name:" -ForegroundColor Green
    $qwenFiles | ForEach-Object {
        Write-Host "  $($_.FullName) - $($_.'Size(MB)') MB" -ForegroundColor White
    }
} else {
    Write-Host "  No files with 'qwen' in name found" -ForegroundColor Gray
}", "is_background": false, "explanation": "Search Windows filesystem for large model files"}

