{"command": "# Also check common download locations in WSL2
Write-Host "`n=== Checking Common WSL2 Locations ===" -ForegroundColor Cyan

$locations = @(
    "~/Downloads",
    "~/downloads", 
    "~/models",
    "~/AI-Models",
    "~/.cache/huggingface",
    "/home/user/Downloads",
    "/home/user/models"
)

foreach ($loc in $locations) {
    Write-Host "Checking: $loc" -ForegroundColor Yellow
    $result = wsl -e bash -c "if [ -d '$loc' ]; then find '$loc' -name '*qwen*' -type f 2>/dev/null | head -5; fi" 2>&1
    if ($result -and $result -notmatch "No such file" -and $result.Length -gt 2) {
        Write-Host "  Γ£à Found:" -ForegroundColor Green
        Write-Host $result
    }
}

# Check for any llamafile files
Write-Host "`n=== Searching for any .llamafile files ===" -ForegroundColor Cyan
$llamafiles = wsl -e bash -c "find ~ /tmp /opt -name '*.llamafile' -type f 2>/dev/null | head -10" 2>&1
if ($llamafiles -and $llamafiles -notmatch "No such file" -and $llamafiles.Length -gt 2) {
    Write-Host "Found .llamafile files:" -ForegroundColor Green
    Write-Host $llamafiles

    # Get details
    foreach ($file in ($llamafiles -split "`n")) {
        if ($file -and $file.Length -gt 1) {
            $file = $file.Trim()
            $details = wsl -e bash -c "ls -lh '$file' 2>/dev/null" 2>&1
            Write-Host "  $details" -ForegroundColor White
        }
    }
} else {
    Write-Host "  No .llamafile files found" -ForegroundColor Gray
}", "is_background": false, "explanation": "Check common download locations and search for any llamafile files"}

