       ```python
       import http.server
       import socketserver
       import requests
       import json

       QWEN_SERVER_HOST = "localhost"
       QWEN_SERVER_PORT = 8080
       PROXY_PORT = 8081

       class ProxyHandler(http.server.SimpleHTTPRequestHandler):
           def do_GET(self):
               if self.path == "/v1/models":
                   self._send_models_response()
               else:
                   self._proxy_request()

           def do_POST(self):
               if self.path == "/v1/chat/completions":
                   self._handle_chat_completions()
               else:
                   self._proxy_request()

           def _send_models_response(self):
               self.send_response(200)
               self.send_header("Content-type", "application/json")
               self.end_headers()
               response_data = {
                   "data": [
                       {
                           "id": "gpt-3.5-turbo",
                           "object": "model",
                           "created": 1677610602,
                           "owned_by": "openai",
                           "permission": [
                               {
                                   "id": "modelperm-M56PvRqc2yZQj9gJLk1hE1Vj",
                                   "object": "model_permission",
                                   "created": 1677610602,
                                   "allow_create_engine": False,
                                   "allow_sampling": True,
                                   "allow_logprobs": True,
                                   "allow_search_indices": False,
                                   "allow_view": True,
                                   "allow_fine_tuning": False,
                                   "organization": "*",
                                   "group": None,
                                   "is_blocking": False
                               }
                           ],
                           "root": "gpt-3.5-turbo",
                           "parent": None
                       }
                   ],
                   "object": "list"
               }
               self.wfile.write(json.dumps(response_data).encode("utf-8"))

           def _handle_chat_completions(self):
               content_length = int(self.headers['Content-Length'])
               post_data = self.rfile.read(content_length)
               request_payload = json.loads(post_data)

               # Modify the model name in the request to Qwen's actual model if needed
               # For llamafile, it often doesn't care about the model name in the request body
               # but if it did, you'd change request_payload['model'] here.

               try:
                   qwen_response = requests.post(
                       f"http://{QWEN_SERVER_HOST}:{QWEN_SERVER_PORT}{self.path}",
                       json=request_payload,
                       headers={'Content-Type': 'application/json'}
                   )
                   qwen_response.raise_for_status()

                   response_data = qwen_response.json()

                   # Modify the model name in the response
                   if 'model' in response_data:
                       response_data['model'] = 'gpt-3.5-turbo'
                   if 'choices' in response_data:
                       for choice in response_data['choices']:
                           if 'message' in choice and 'model' in choice['message']:
                               choice['message']['model'] = 'gpt-3.5-turbo'

                   self.send_response(qwen_response.status_code)
                   for header, value in qwen_response.headers.items():
                       if header.lower() not in ['transfer-encoding', 'content-encoding', 'content-length']:
                           self.send_header(header, value)
                   self.end_headers()
                   self.wfile.write(json.dumps(response_data).encode("utf-8"))

               except requests.exceptions.RequestException as e:
                   self.send_error(500, f"Proxy error: {e}")

           def _proxy_request(self):
               try:
                   target_url = f"http://{QWEN_SERVER_HOST}:{QWEN_SERVER_PORT}{self.path}"

                   if self.command == 'GET':
                       resp = requests.get(target_url, headers=self.headers)
                   elif self.command == 'POST':
                       content_length = int(self.headers['Content-Length'])
                       post_data = self.rfile.read(content_length)
                       resp = requests.post(target_url, data=post_data, headers=self.headers)
                   else:
                       self.send_error(405, "Method Not Allowed")
                       return

                   self.send_response(resp.status_code)
                   for header, value in resp.headers.items():
                       if header.lower() not in ['transfer-encoding', 'content-encoding', 'content-length']:
                           self.send_header(header, value)
                   self.end_headers()
                   self.wfile.write(resp.content)

               except requests.exceptions.RequestException as e:
                   self.send_error(500, f"Proxy error: {e}")

       if __name__ == "__main__":
           with socketserver.TCPServer(("", PROXY_PORT), ProxyHandler) as httpd:
               print(f"Serving Qwen proxy on port {PROXY_PORT}")
               print(f"Proxying requests to Qwen server on {QWEN_SERVER_HOST}:{QWEN_SERVER_PORT}")
               httpd.serve_forever()
       ```
