{"command": "# Check for hooks/enforce-linting script specifically
if (Test-Path "hooks\enforce-linting-v7.ps1") {
    Write-Host "=== Found enforce-linting-v7.ps1 ===" -ForegroundColor Green
    Write-Host "`nScript contents (first 50 lines):" -ForegroundColor Cyan
    Get-Content "hooks\enforce-linting-v7.ps1" | Select-Object -First 50
} elseif (Test-Path "hooks\enforce-linting.sh") {
    Write-Host "=== Found enforce-linting.sh ===" -ForegroundColor Green
    Get-Content "hooks\enforce-linting.sh" | Select-Object -First 50
}

# Check package.json for lint scripts
if (Test-Path "package.json") {
    Write-Host "`n=== package.json lint scripts ===" -ForegroundColor Cyan
    $pkg = Get-Content "package.json" | ConvertFrom-Json
    if ($pkg.scripts) {
        $pkg.scripts.PSObject.Properties | Where-Object { $_.Name -match "lint" } | ForEach-Object {
            Write-Host "  $($_.Name): $($_.Value)" -ForegroundColor White
        }
    }
}

# Check for ESLint/TSLint configs
Write-Host "`n=== Linter config files ===" -ForegroundColor Cyan
Get-ChildItem -Recurse -Include .eslintrc*,eslint.config.*,.tslint.json,tsconfig.json -ErrorAction SilentlyContinue | 
    Select-Object FullName | 
    ForEach-Object { $_.FullName.Replace((Get-Location).Path + "\", "") }", "is_background": false, "explanation": "Examine linting scripts and configurations"}

