{"command": "# Check if there are any large binary files that might be the model
Write-Host "`n=== Checking for Large Binary Files (4-5GB range) ===" -ForegroundColor Cyan

# Check common locations for large files
$checkPaths = @(
    "C:\Users\user\Downloads",
    "C:\Users\user\Documents",
    "D:\",
    "C:\Users\user\AI-Models"
)

foreach ($path in $checkPaths) {
    if (Test-Path $path) {
        Write-Host "`nChecking: $path" -ForegroundColor Yellow
        try {
            $largeFiles = Get-ChildItem -Path $path -Recurse -File -ErrorAction SilentlyContinue | 
                Where-Object { $_.Length -gt 4GB -and $_.Length -lt 6GB } | 
                Select-Object FullName, @{N="Size(GB)";E={[math]::Round($_.Length/1GB,2)}}, LastWriteTime -First 3

            if ($largeFiles) {
                Write-Host "  Found large files (4-5GB):" -ForegroundColor Green
                $largeFiles | ForEach-Object {
                    Write-Host "    $($_.FullName) - $($_.'Size(GB)') GB" -ForegroundColor White
                }
            }
        } catch {
            Write-Host "  ΓÜá∩╕Å Error: $_" -ForegroundColor Yellow
        }
    }
}", "is_background": false, "explanation": "Check for large binary files in the 4-5GB range that might be the model"}

