        .code-directive { color: #d2a8ff; }
        .code-string { color: #a5d6ff; }
        .code-comment { color: var(--text-muted); font-style: italic; }
        .code-register { color: #7ee787; }
        .code-number { color: #79c0ff; }
        .code-label { color: #ffa657; }
        
        /* Templates */
        .templates {
            padding: 6rem 2rem;
            max-width: 1400px;
            margin: 0 auto;
        }
        
        .template-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            gap: 1rem;
        }
        
        .template-card {
            background: var(--bg-card);
            border: 1px solid var(--border-color);
            border-radius: 12px;
            padding: 1.5rem;
            text-align: center;
            transition: all 0.2s;
            cursor: pointer;
        }
        
        .template-card:hover {
            border-color: var(--accent-primary);
            background: var(--bg-tertiary);
        }
        
        .template-card.active {
            border-color: var(--accent-primary);
            background: rgba(0, 217, 255, 0.1);
        }
        
        .template-icon {
            font-size: 2rem;
            margin-bottom: 0.75rem;
        }
        
        .template-name {
            font-weight: 600;
            margin-bottom: 0.25rem;
        }
        
        .template-desc {
            font-size: 0.75rem;
            color: var(--text-muted);
        }
        
        /* CTA */
        .cta {
            padding: 6rem 2rem;
            text-align: center;
            background: linear-gradient(180deg, var(--bg-secondary) 0%, var(--bg-primary) 100%);
        }
        
        .cta-inner {
            max-width: 800px;
            margin: 0 auto;
            padding: 4rem;
            background: var(--bg-card);
            border: 1px solid var(--border-color);
            border-radius: 24px;
            position: relative;
            overflow: hidden;
        }
        
        .cta-inner::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            height: 2px;
            background: var(--accent-gradient);
        }
        
        .cta h2 {
            font-size: 2rem;
            margin-bottom: 1rem;
        }
        
        .cta p {
