        Visit ID: <span id="visit-id">--</span><br>
        Time on page: <span id="time-on-page">0s</span>
    </div>

    <script>
        // MASM64 Framework Landing Page with Puter.js Analytics
        (async function() {
            'use strict';
            
            // Analytics state
            const analytics = {
                visitId: crypto.randomUUID(),
                startTime: Date.now(),
                events: [],
                scrollDepth: 0,
                clicks: 0
            };
            
            // Track page view
            function trackEvent(eventName, data = {}) {
                const event = {
                    event: eventName,
                    timestamp: Date.now(),
                    visitId: analytics.visitId,
                    url: window.location.href,
                    referrer: document.referrer,
                    userAgent: navigator.userAgent,
                    screenWidth: window.innerWidth,
                    screenHeight: window.innerHeight,
                    ...data
                };
                analytics.events.push(event);
                
                // Store in Puter KV if authenticated
                storeAnalytics(event);
            }
            
            // Store analytics using Puter.js
            async function storeAnalytics(event) {
                try {
                    if (typeof puter !== 'undefined' && puter.kv) {
                        const key = `masm64_analytics_${analytics.visitId}_${Date.now()}`;
                        await puter.kv.set(key, JSON.stringify(event));
                    }
                } catch (e) {
                    // Silent fail - analytics should not break the page
                    console.debug('Analytics store:', e.message);
                }
            }
            
            // Track scroll depth
            let maxScrollDepth = 0;
            window.addEventListener('scroll', () => {
                const scrollHeight = document.documentElement.scrollHeight - window.innerHeight;
                const scrollPercent = Math.round((window.scrollY / scrollHeight) * 100);
                if (scrollPercent > maxScrollDepth) {
                    maxScrollDepth = scrollPercent;
                    if (maxScrollDepth % 25 === 0) {
                        trackEvent('scroll_milestone', { depth: maxScrollDepth });
                    }
                }
            });
            
            // Track clicks on key elements
            document.addEventListener('click', (e) => {
                const target = e.target.closest('a, button, .template-card');
                if (target) {
                    analytics.clicks++;
                    const data = {
                        element: target.tagName,
                        text: target.textContent?.substring(0, 50),
                        href: target.href || null,
                        className: target.className
                    };
                    trackEvent('click', data);
                }
            });
            
            // Template card interaction
            document.querySelectorAll('.template-card').forEach(card => {
                card.addEventListener('click', () => {
                    document.querySelectorAll('.template-card').forEach(c => c.classList.remove('active'));
                    card.classList.add('active');
                    trackEvent('template_selected', { template: card.dataset.template });
                });
            });
            
            // Track time on page
            function updateTimeOnPage() {
                const seconds = Math.floor((Date.now() - analytics.startTime) / 1000);
                const el = document.getElementById('time-on-page');
                if (el) {
                    if (seconds < 60) {
                        el.textContent = `${seconds}s`;
                    } else {
                        el.textContent = `${Math.floor(seconds/60)}m ${seconds%60}s`;
                    }
                }
            }
            setInterval(updateTimeOnPage, 1000);
            
            // Show visit ID
            document.getElementById('visit-id').textContent = analytics.visitId.substring(0, 8);
            
            // Track page view on load
            trackEvent('page_view', {
                title: document.title,
                path: window.location.pathname
            });
            
            // Track session end
            window.addEventListener('beforeunload', () => {
                trackEvent('session_end', {
                    duration: Date.now() - analytics.startTime,
                    maxScrollDepth: maxScrollDepth,
                    totalClicks: analytics.clicks
                });
                
                // Attempt to send final beacon
                if (navigator.sendBeacon) {
                    const data = JSON.stringify({
                        visitId: analytics.visitId,
                        events: analytics.events,
                        summary: {
                            duration: Date.now() - analytics.startTime,
                            maxScrollDepth,
                            clicks: analytics.clicks
                        }
                    });
                    // Could send to a backend endpoint
                }
            });
            
            // Smooth scroll for nav links
            document.querySelectorAll('nav a[href^="#"]').forEach(anchor => {
                anchor.addEventListener('click', (e) => {
                    const href = anchor.getAttribute('href');
                    if (href.startsWith('#')) {
                        e.preventDefault();
                        const target = document.querySelector(href);
                        if (target) {
                            target.scrollIntoView({ behavior: 'smooth' });
                        }
                    }
                });
            });
            
            // Admin analytics view - owner only via Puter.js auth
            const OWNER_USERNAME = 'ludoplex';
            let isOwner = false;
            
            async function checkOwnerAccess() {
                try {
                    if (typeof puter !== 'undefined' && puter.auth) {
                        const user = await puter.auth.getUser();
                        if (user && user.username === OWNER_USERNAME) {
                            isOwner = true;
                            document.getElementById('analytics-panel').classList.add('visible');
                            console.log('%cOwner access granted', 'color: #3fb950;');
                        }
                    }
                } catch (e) {
                    // Not authenticated or error - that's fine
                }
            }
            
            // Check on load
            checkOwnerAccess();
            
            console.log('%cMASM64 Framework', 'font-size: 24px; font-weight: bold; color: #00d9ff;');
        })();
    </script>
</body>
</html>

"}

