                        <span class="code-title">main.asm</span>
                    </div>
                    <div class="code-content">
<pre><span class="code-comment">; Simple MASM64 Framework Example</span>
<span class="code-directive">INCLUDE</span> core\abi64.inc
<span class="code-directive">INCLUDE</span> core\stack64.inc
<span class="code-directive">INCLUDE</span> lib\arena64\arena64.inc

<span class="code-directive">.DATA</span>
<span class="code-label">szHello</span>    <span class="code-keyword">DB</span> <span class="code-string">"Hello from MASM64!"</span>, <span class="code-number">0</span>

<span class="code-directive">.CODE</span>
<span class="code-label">main</span> <span class="code-keyword">PROC</span> <span class="code-keyword">FRAME</span>
    <span class="code-comment">; Prologue with proper stack alignment</span>
    <span class="code-keyword">PROLOGUE</span> <span class="code-number">0</span>, <span class="code-number">64</span>
    
    <span class="code-comment">; Create arena allocator (1MB)</span>
    <span class="code-keyword">mov</span> <span class="code-register">rcx</span>, <span class="code-number">1024 * 1024</span>
    <span class="code-keyword">mov</span> <span class="code-register">rdx</span>, <span class="code-number">64 * 1024</span>
    <span class="code-keyword">mov</span> <span class="code-register">r8d</span>, ARENA_GROW_COMMIT
    <span class="code-keyword">call</span> Arena_Create
    <span class="code-keyword">ASSERT_RAX</span>                  <span class="code-comment">; Verify allocation</span>
    
    <span class="code-comment">; Print message</span>
    <span class="code-keyword">PRINT_STRING</span> szHello
    
    <span class="code-comment">; Epilogue and exit</span>
    <span class="code-keyword">EPILOGUE</span>
    <span class="code-keyword">EXIT_PROCESS</span> <span class="code-number">0</span>
<span class="code-label">main</span> <span class="code-keyword">ENDP</span>
<span class="code-keyword">END</span></pre>
