{"command": "# Check if telnet command exists
$telnetPath = Get-Command telnet -ErrorAction SilentlyContinue
if ($telnetPath) {
    Write-Host "Telnet available at: $($telnetPath.Source)"
} else {
    Write-Host "Telnet not available. Alternatives:"
    Write-Host "1. Use PuTTY (putty.exe -telnet 192.168.1.79)"
    Write-Host "2. Use netcat: Test-NetConnection 192.168.1.79 -Port 23"
    Write-Host "3. Enable telnet: dism /online /Enable-Feature /FeatureName:TelnetClient"
}", "is_background": false, "explanation": "Check if telnet command is available on Windows"}

