{"command": "# Search entire Cursor directory for any file containing "baseUrl" or custom model config
$paths = @(
    "$env:APPDATA\Cursor",
    "$env:USERPROFILE\.cursor",
    "$env:LOCALAPPDATA\Cursor"
)
foreach ($path in $paths) {
    if (Test-Path $path) {
        $files = Get-ChildItem $path -Recurse -File -ErrorAction SilentlyContinue | Where-Object { $_.Extension -match 'json|txt|cfg|ini' -and $_.Length -lt 100KB }
        foreach ($f in $files) {
            $content = Get-Content $f.FullName -Raw -ErrorAction SilentlyContinue
            if ($content -match 'baseUrl|customModel|openaiApiKey|local-qwen') {
                Write-Host "FOUND: $($f.FullName)"
                Write-Host ($content | Select-String -Pattern 'baseUrl|customModel|openaiApiKey|local-qwen' -AllMatches).Line
            }
        }
    }
}", "is_background": false, "explanation": "Deep search for model config in all Cursor directories"}

