$LogFile = "$env:TEMP\rustdesk-watchdog.log"
$RestartCount = 0
$RestartWindow = Get-Date

function Write-Log {
    param($Message)
    $Timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
    $LogMessage = "[$Timestamp] $Message"
    Write-Host $LogMessage
    Add-Content -Path $LogFile -Value $LogMessage
}

function Test-RustDeskConnection {
    # Check if RustDesk service is running
    $service = Get-Service -Name "RustDesk" -ErrorAction SilentlyContinue
    if (-not $service -or $service.Status -ne 'Running') {
        return $false
    }

    # Check if RustDesk processes exist
    $processes = Get-Process -Name "rustdesk" -ErrorAction SilentlyContinue
    if (-not $processes) {
        return $false
    }

    # Check for established connections to relay server
    $connections = netstat -ano | Select-String "2111" | Select-String "ESTABLISHED"
    if (-not $connections) {
        Write-Log "No established connections to relay server"
        return $false
    }

    return $true
}

function Restart-RustDeskService {
    Write-Log "Attempting to restart RustDesk service..."

    try {
        # Stop all RustDesk processes
        Get-Process -Name "rustdesk" -ErrorAction SilentlyContinue | Stop-Process -Force
        Start-Sleep -Seconds 2

        # Restart service
        Restart-Service -Name "RustDesk" -Force -ErrorAction Stop
        Start-Sleep -Seconds 5

        # Start GUI process
        Start-Process "C:\Program Files\RustDesk\RustDesk.exe" -WindowStyle Hidden

        Write-Log "RustDesk service restarted successfully"
        return $true
    }
    catch {
        Write-Log "ERROR: Failed to restart RustDesk - $_"
        return $false
    }
}

function Reset-RestartCounter {
    $now = Get-Date
    if (($now - $RestartWindow).TotalMinutes -gt 60) {
        $script:RestartCount = 0
        $script:RestartWindow = $now
        Write-Log "Restart counter reset"
    }
}

# Main monitoring loop
Write-Log "=== RustDesk Watchdog Started ==="
Write-Log "Check interval: $CheckInterval seconds"
Write-Log "Max restarts per hour: $MaxRestarts"

while ($true) {
    Reset-RestartCounter

    $isConnected = Test-RustDeskConnection

    if (-not $isConnected) {
        Write-Log "WARNING: RustDesk connection lost!"

        if ($RestartCount -lt $MaxRestarts) {
            if (Restart-RustDeskService) {
                $script:RestartCount++
                Write-Log "Restart count: $RestartCount / $MaxRestarts"

                # Wait longer after restart
                Start-Sleep -Seconds 30
            }
            else {
                Write-Log "ERROR: Restart failed, waiting before retry"
                Start-Sleep -Seconds $CheckInterval
            }
        }
        else {
            Write-Log "ERROR: Max restarts reached ($MaxRestarts). Manual intervention required."
            Write-Log "Waiting 60 minutes before allowing more restarts..."
            Start-Sleep -Seconds 3600
        }
    }
    else {
        Write-Log "OK: RustDesk connection active"
    }

    Start-Sleep -Seconds $CheckInterval
}

"}

