# Local Network Port Scanner
# Scans for SSH (22), Telnet (23), and HTTPS (443) ports

param(
    [string]$Network = "192.168.1",
    [int]$StartIP = 1,
    [int]$EndIP = 254,
    [int]$Timeout = 100
)

$Ports = @{
    22 = "SSH"
    23 = "Telnet"
    443 = "HTTPS"
}

Write-Host "=== Local Network Port Scanner ===" -ForegroundColor Cyan
Write-Host "Network: $Network.0/24" -ForegroundColor Yellow
Write-Host "Scanning for: SSH (22), Telnet (23), HTTPS (443)" -ForegroundColor Yellow
Write-Host "Timeout: $Timeout ms per port`n" -ForegroundColor Yellow

$Results = @()

for ($i = $StartIP; $i -le $EndIP; $i++) {
    $IP = "$Network.$i"
    $HostUp = $false

    # Scan ports directly (faster than ping on Windows)
    foreach ($Port in $Ports.Keys) {
        try {
            $TcpClient = New-Object System.Net.Sockets.TcpClient
            $Connect = $TcpClient.BeginConnect($IP, $Port, $null, $null)
            $Wait = $Connect.AsyncWaitHandle.WaitOne($Timeout, $false)

            if ($Wait -and $TcpClient.Connected) {
                if (-not $HostUp) {
                    Write-Host "[$IP] Host is up - found open ports" -ForegroundColor Green
                    $HostUp = $true
                }

                $Service = $Ports[$Port]
                Write-Host "  [+] $IP`:$Port ($Service) - OPEN" -ForegroundColor Green

                $Results += [PSCustomObject]@{
                    IP = $IP
                    Port = $Port
                    Service = $Service
                    Status = "OPEN"
                }
            }

            $TcpClient.Close()
        }
        catch {
            # Port closed or filtered
        }
    }

    # Progress indicator
    if ($i % 10 -eq 0) {
        Write-Host "." -NoNewline -ForegroundColor Gray
    }
}

Write-Host "`n=== Scan Results ===" -ForegroundColor Cyan
if ($Results.Count -gt 0) {
    $Results | Format-Table -AutoSize

    Write-Host "`nSummary:" -ForegroundColor Yellow
    Write-Host "  Total hosts scanned: $($EndIP - $StartIP + 1)" -ForegroundColor Cyan
    Write-Host "  Open ports found: $($Results.Count)" -ForegroundColor Green

    # Group by service
    $Results | Group-Object Service | ForEach-Object {
        Write-Host "  $($_.Name): $($_.Count) host(s)" -ForegroundColor Cyan
    }
}
else {
    Write-Host "No open SSH, Telnet, or HTTPS ports found on network." -ForegroundColor Yellow
}

